import React, { useEffect, useState } from 'react';
import { Navigate, useLocation } from 'react-router-dom';
import { useAuth } from '../../hooks/useAuth';

const ProtectedRoute = ({ children }) => {
  const { user, isAuthenticated, loading, isTokenValid } = useAuth();
  const location = useLocation();
  const [isChecking, setIsChecking] = useState(true);

  useEffect(() => {
    // FIXED: Better authentication check with proper timing
    const checkAuth = () => {
      console.log('üõ°Ô∏è ProtectedRoute: Checking authentication...');
      console.log('User:', !!user);
      console.log('IsAuthenticated:', isAuthenticated);
      console.log('Token valid:', isTokenValid());
      console.log('Loading:', loading);

      // Give the auth system time to validate the token
      setTimeout(() => {
        setIsChecking(false);
      }, 500);
    };

    checkAuth();
  }, [user, isAuthenticated, isTokenValid, loading]);

  // Show loading spinner while checking authentication
  if (loading || isChecking) {
    return (
      <div style={{ 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        height: '100vh' 
      }}>
        <div>Loading...</div>
      </div>
    );
  }

  // FIXED: More robust authentication check
  const hasValidAuth = user && isAuthenticated && isTokenValid();

  console.log('üõ°Ô∏è ProtectedRoute: Final auth decision:', hasValidAuth);

  if (!hasValidAuth) {
    console.log('üõ°Ô∏è ProtectedRoute: Redirecting to login');
    // Redirect to login with return path
    return <Navigate to="/" state={{ from: location }} replace />;
  }

  console.log('üõ°Ô∏è ProtectedRoute: Allowing access');
  return children;
};

export default ProtectedRoute;
