// =================== ChallengeGrid.jsx ===================
import React, { useState } from 'react';
import { 
  Grid, 
  Typography, 
  Box, 
  Tabs, 
  Tab,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  TextField,
  InputAdornment
} from '@mui/material';
import { Search, FilterList } from '@mui/icons-material';
import { useSelector } from 'react-redux';
import ChallengeCard from '../challenges/ChallengeCard';

const ChallengeGrid = ({ maxChallenges = 6, showFilters = true }) => {
  const { challenges } = useSelector(state => state.challenges);
  
  const [tabValue, setTabValue] = useState(0);
  const [difficultyFilter, setDifficultyFilter] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  
  // Filter challenges based on current tab
  const getFilteredChallenges = () => {
    if (!challenges) return [];
    
    let filtered = [...challenges];
    
    // Filter by tab (status)
    switch (tabValue) {
      case 0: // All challenges
        break;
      case 1: // Available
        filtered = filtered.filter(c => c.is_unlocked && !c.is_completed);
        break;
      case 2: // Completed
        filtered = filtered.filter(c => c.is_completed);
        break;
      case 3: // Locked
        filtered = filtered.filter(c => !c.is_unlocked);
        break;
      default:
        break;
    }
    
    // Filter by difficulty
    if (difficultyFilter !== 'all') {
      filtered = filtered.filter(c => c.difficulty === difficultyFilter);
    }
    
    // Filter by search term
    if (searchTerm) {
      const term = searchTerm.toLowerCase();
      filtered = filtered.filter(c => 
        c.title?.toLowerCase().includes(term) ||
        c.description?.toLowerCase().includes(term)
      );
    }
    
    // Limit results
    return filtered.slice(0, maxChallenges);
  };
  
  const filteredChallenges = getFilteredChallenges();
  
  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };
  
  const getTabCounts = () => {
    if (!challenges) return { all: 0, available: 0, completed: 0, locked: 0 };
    
    return {
      all: challenges.length,
      available: challenges.filter(c => c.is_unlocked && !c.is_completed).length,
      completed: challenges.filter(c => c.is_completed).length,
      locked: challenges.filter(c => !c.is_unlocked).length
    };
  };
  
  const tabCounts = getTabCounts();

  return (
    <Box sx={{ mb: 4 }}>
      <Typography variant="h5" gutterBottom sx={{ mb: 3, fontWeight: 'bold' }}>
        ðŸŽ¯ Your Challenges
      </Typography>
      
      {showFilters && (
        <Box sx={{ mb: 3 }}>
          {/* Status Tabs */}
          <Tabs 
            value={tabValue} 
            onChange={handleTabChange} 
            sx={{ mb: 2 }}
            variant="scrollable"
            scrollButtons="auto"
          >
            <Tab label={`All (${tabCounts.all})`} />
            <Tab label={`Available (${tabCounts.available})`} />
            <Tab label={`Completed (${tabCounts.completed})`} />
            <Tab label={`Locked (${tabCounts.locked})`} />
          </Tabs>
          
          {/* Filters */}
          <Grid container spacing={2} sx={{ mb: 2 }}>
            <Grid item xs={12} sm={6} md={4}>
              <TextField
                fullWidth
                placeholder="Search challenges..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <Search />
                    </InputAdornment>
                  ),
                }}
                size="small"
              />
            </Grid>
            
            <Grid item xs={12} sm={6} md={4}>
              <FormControl fullWidth size="small">
                <InputLabel>Difficulty</InputLabel>
                <Select
                  value={difficultyFilter}
                  label="Difficulty"
                  onChange={(e) => setDifficultyFilter(e.target.value)}
                  startAdornment={
                    <InputAdornment position="start">
                      <FilterList />
                    </InputAdornment>
                  }
                >
                  <MenuItem value="all">All Difficulties</MenuItem>
                  <MenuItem value="beginner">Beginner</MenuItem>
                  <MenuItem value="intermediate">Intermediate</MenuItem>
                  <MenuItem value="advanced">Advanced</MenuItem>
                  <MenuItem value="expert">Expert</MenuItem>
                </Select>
              </FormControl>
            </Grid>
          </Grid>
        </Box>
      )}
      
      {/* Challenge Grid */}
      {filteredChallenges.length > 0 ? (
        <Grid container spacing={3}>
          {filteredChallenges.map((challenge) => (
            <Grid item xs={12} sm={6} md={4} key={challenge.id}>
              <ChallengeCard challenge={challenge} />
            </Grid>
          ))}
        </Grid>
      ) : (
        <Box sx={{ textAlign: 'center', py: 4 }}>
          <Typography variant="h6" color="text.secondary" gutterBottom>
            No challenges found
          </Typography>
          <Typography variant="body2" color="text.disabled">
            {searchTerm || difficultyFilter !== 'all' 
              ? 'Try adjusting your filters to see more results.'
              : 'Complete more challenges to unlock new content!'}
          </Typography>
        </Box>
      )}
      
      {/* Show more indicator */}
      {challenges && filteredChallenges.length === maxChallenges && challenges.length > maxChallenges && (
        <Box sx={{ textAlign: 'center', mt: 3 }}>
          <Typography variant="body2" color="text.secondary">
            Showing {maxChallenges} of {challenges.length} challenges
          </Typography>
        </Box>
      )}
    </Box>
  );
};

export default ChallengeGrid;
