// src/pages/ChallengePage.jsx
import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useParams, useNavigate } from 'react-router-dom';
import { 
  Container, 
  Box, 
  Typography, 
  Paper, 
  Button, 
  Alert,
  Grid,
  Chip
} from '@mui/material';
import { ArrowBack, PlayArrow, Stop } from '@mui/icons-material';
import challengeService from '../services/challengeService';
import LoadingSpinner from '../components/common/LoadingSpinner';

const ChallengePage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const { user } = useSelector(state => state.auth);

  // Local state
  const [challenge, setChallenge] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [attempting, setAttempting] = useState(false);

  useEffect(() => {
    loadChallenge();
  }, [id]);

  const loadChallenge = async () => {
    try {
      setLoading(true);
      setError(null);
      console.log('Loading challenge with ID:', id);

      const response = await challengeService.getChallenge(id);
      console.log('Challenge loaded:', response.data);
      setChallenge(response.data);
    } catch (error) {
      console.error('Failed to load challenge:', error);
      setError('Failed to load challenge. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const startChallenge = async () => {
    try {
      setAttempting(true);
      setError(null);
      console.log('Starting challenge:', id);

      const response = await challengeService.startChallenge(id);
      console.log('Challenge started:', response.data);

      // Navigate to the individual challenge runner page
      navigate(`/challenges/${id}`);
    } catch (error) {
      console.error('Failed to start challenge:', error);
      setError('Failed to start challenge. Please try again.');
    } finally {
      setAttempting(false);
    }
  };

  const getDifficultyColor = (difficulty) => {
    const colors = {
      beginner: 'success',
      intermediate: 'warning', 
      advanced: 'error',
      expert: 'secondary'
    };
    return colors[difficulty?.toLowerCase()] || 'default';
  };

  // Loading state
  if (loading) {
    return (
      <Container maxWidth="lg">
        <Box className="flex-center" style={{ minHeight: '60vh' }}>
          <LoadingSpinner message="Loading challenge..." />
        </Box>
      </Container>
    );
  }

  // Error state
  if (error) {
    return (
      <Container maxWidth="lg">
        <Box sx={{ py: 4 }}>
          <Alert severity="error" sx={{ mb: 3 }}>
            {error}
          </Alert>
          <Box sx={{ display: 'flex', gap: 2 }}>
            <Button variant="outlined" onClick={() => navigate('/dashboard')}>
              Back to Dashboard
            </Button>
            <Button variant="contained" onClick={loadChallenge}>
              Retry
            </Button>
          </Box>
        </Box>
      </Container>
    );
  }

  // No challenge found
  if (!challenge) {
    return (
      <Container maxWidth="lg">
        <Box sx={{ py: 4 }}>
          <Alert severity="warning" sx={{ mb: 3 }}>
            Challenge not found. The challenge with ID "{id}" does not exist or you don't have access to it.
          </Alert>
          <Button variant="outlined" onClick={() => navigate('/dashboard')}>
            Back to Dashboard
          </Button>
        </Box>
      </Container>
    );
  }

  return (
    <Container maxWidth="lg">
      <Box sx={{ py: 4 }}>
        {/* Header */}
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 4 }}>
          <Button
            startIcon={<ArrowBack />}
            onClick={() => navigate('/dashboard')}
            sx={{ mr: 2 }}
          >
            Back to Dashboard
          </Button>
          <Box>
            <Typography variant="h4" component="h1">
              {challenge.challenge?.title || challenge.title || `Challenge ${id}`}
            </Typography>
            <Box sx={{ display: 'flex', gap: 1, mt: 1 }}>
              <Chip 
                label={challenge.challenge?.difficulty || challenge.difficulty || 'beginner'} 
                color={getDifficultyColor(challenge.challenge?.difficulty || challenge.difficulty)}
                size="small"
              />
              <Chip 
                label={challenge.challenge?.type || challenge.type || 'manual_movement'} 
                variant="outlined"
                size="small"
              />
              {challenge.challenge?.points && (
                <Chip 
                  label={`${challenge.challenge.points} points`}
                  color="primary"
                  variant="outlined"
                  size="small"
                />
              )}
            </Box>
          </Box>
        </Box>

        <Grid container spacing={4}>
          {/* Main Content */}
          <Grid item xs={12} md={8}>
            <Paper sx={{ p: 4, bgcolor: 'background.paper' }}>
              <Typography variant="h6" gutterBottom>
                Challenge Description
              </Typography>
              <Typography variant="body1" sx={{ mb: 3, lineHeight: 1.7 }}>
                {challenge.challenge?.description || challenge.description || 
                 'This challenge will test your robotics skills. Follow the instructions and complete the tasks to earn points.'}
              </Typography>

              {/* Instructions */}
              {challenge.challenge?.instructions && (
                <Box sx={{ mb: 3 }}>
                  <Typography variant="h6" gutterBottom>
                    Instructions
                  </Typography>
                  <Typography variant="body1" sx={{ mb: 2, lineHeight: 1.7 }}>
                    {challenge.challenge.instructions}
                  </Typography>
                </Box>
              )}

              {/* Video URL */}
              {challenge.challenge?.video_url && (
                <Box sx={{ mb: 3 }}>
                  <Typography variant="h6" gutterBottom>
                    Instructional Video
                  </Typography>
                  <Box
                    sx={{
                      position: 'relative',
                      paddingBottom: '56.25%',
                      height: 0,
                      overflow: 'hidden',
                      borderRadius: 1,
                      bgcolor: 'rgba(255,255,255,0.1)'
                    }}
                  >
                    <iframe
                      src={challenge.challenge.video_url}
                      style={{
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        width: '100%',
                        height: '100%',
                        border: 'none'
                      }}
                      allowFullScreen
                      title="Challenge Instructions"
                    />
                  </Box>
                </Box>
              )}

              {/* Challenge Interface Placeholder */}
              <Box sx={{ 
                mt: 4, 
                p: 3, 
                bgcolor: 'rgba(255,255,255,0.05)', 
                borderRadius: 2,
                textAlign: 'center'
              }}>
                <Typography variant="h6" sx={{ mb: 2 }}>
                  ü§ñ Challenge Interface
                </Typography>
                <Typography variant="body2" color="text.secondary" sx={{ mb: 3 }}>
                  The challenge interface will appear here when you start the challenge.
                  This could include robot controls, quiz questions, or other interactive elements.
                </Typography>

                <Button
                  variant="contained"
                  size="large"
                  startIcon={attempting ? <Stop /> : <PlayArrow />}
                  onClick={startChallenge}
                  disabled={attempting || (challenge.is_unlocked === false)}
                  sx={{ minWidth: 200 }}
                >
                  {attempting ? 'Starting...' : 'Start Challenge'}
                </Button>

                {challenge.is_unlocked === false && (
                  <Typography variant="caption" display="block" sx={{ mt: 1 }} color="warning.main">
                    Complete previous challenges to unlock this one
                  </Typography>
                )}
              </Box>
            </Paper>
          </Grid>

          {/* Sidebar */}
          <Grid item xs={12} md={4}>
            {/* Progress Card */}
            <Paper sx={{ p: 3, bgcolor: 'background.paper', mb: 3 }}>
              <Typography variant="h6" gutterBottom>
                üìä Your Progress
              </Typography>

              <Box sx={{ mb: 2 }}>
                <Typography variant="body2" color="text.secondary">
                  Best Score
                </Typography>
                <Typography variant="h4" color="primary">
                  {challenge.user_progress?.best_score || challenge.best_score || 0}
                </Typography>
              </Box>

              <Box sx={{ mb: 2 }}>
                <Typography variant="body2" color="text.secondary">
                  Attempts
                </Typography>
                <Typography variant="h6">
                  {challenge.user_progress?.total_attempts || challenge.attempts || 0}
                </Typography>
              </Box>

              <Box sx={{ mb: 2 }}>
                <Typography variant="body2" color="text.secondary">
                  Status
                </Typography>
                <Chip 
                  label={
                    challenge.user_progress?.is_completed || challenge.is_completed 
                      ? 'Completed' 
                      : challenge.is_unlocked !== false 
                        ? 'Available' 
                        : 'Locked'
                  }
                  color={
                    challenge.user_progress?.is_completed || challenge.is_completed 
                      ? 'success' 
                      : challenge.is_unlocked !== false 
                        ? 'primary' 
                        : 'default'
                  }
                  size="small"
                />
              </Box>

              {challenge.user_progress?.completion_date && (
                <Box sx={{ mb: 2 }}>
                  <Typography variant="body2" color="text.secondary">
                    Completed On
                  </Typography>
                  <Typography variant="body2">
                    {new Date(challenge.user_progress.completion_date).toLocaleDateString()}
                  </Typography>
                </Box>
              )}
            </Paper>

            {/* Challenge Info */}
            <Paper sx={{ p: 3, bgcolor: 'background.paper' }}>
              <Typography variant="h6" gutterBottom>
                ‚ÑπÔ∏è Challenge Info
              </Typography>

              <Box sx={{ mb: 2 }}>
                <Typography variant="body2" color="text.secondary">
                  Estimated Time
                </Typography>
                <Typography variant="body1">
                  {challenge.challenge?.estimated_time || challenge.estimated_time || 30} minutes
                </Typography>
              </Box>

              <Box sx={{ mb: 2 }}>
                <Typography variant="body2" color="text.secondary">
                  Difficulty
                </Typography>
                <Typography variant="body1" sx={{ textTransform: 'capitalize' }}>
                  {challenge.challenge?.difficulty || challenge.difficulty || 'Beginner'}
                </Typography>
              </Box>

              <Box sx={{ mb: 2 }}>
                <Typography variant="body2" color="text.secondary">
                  Type
                </Typography>
                <Typography variant="body1" sx={{ textTransform: 'capitalize' }}>
                  {(challenge.challenge?.type || challenge.type || 'manual_movement').replace('_', ' ')}
                </Typography>
              </Box>

              {challenge.challenge?.max_score && (
                <Box sx={{ mb: 2 }}>
                  <Typography variant="body2" color="text.secondary">
                    Max Score
                  </Typography>
                  <Typography variant="body1">
                    {challenge.challenge.max_score} points
                  </Typography>
                </Box>
              )}
            </Paper>

            {/* Debug Info - Remove in production */}
            {process.env.NODE_ENV === 'development' && (
              <Paper sx={{ p: 2, bgcolor: 'rgba(255,255,255,0.05)', mt: 2 }}>
                <Typography variant="caption" gutterBottom display="block">
                  üîç Debug Info:
                </Typography>
                <Typography variant="caption" component="pre" sx={{ fontSize: '10px', overflow: 'auto' }}>
                  {JSON.stringify(challenge, null, 2)}
                </Typography>
              </Paper>
            )}
          </Grid>
        </Grid>
      </Box>
    </Container>
  );
};

export default ChallengePage;
