import { useState, useEffect, useCallback, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useWebSerial } from './useWebSerial';
import {
  updateConnectionStatus,
  setMotors,
  updateMotor,
  addMotor,
  removeMotor,
  updateChartData,
  addLogEntry,
  incrementCommandCount,
  updateUptime,
  startChallenge,
  stopChallenge as stopChallengeAction,
  updateChallengeProgress,
  updateHardwareStats,
  setEmergencyStop,
  selectRobot,
  selectIsConnected,
  selectMotors,
  selectChallengeStatus,
} from '../store/robotSlice';
import { 
  rawToDegrees, 
  degreesToPositionRaw, 
  degreesToVelocityRaw 
} from '../utils/robotUtils';
import { COMMANDS, ROBOT_SPECS, PERFORMANCE } from '../utils/constants';
import { now, clamp, throttle, debugLog, errorLog } from '../utils/helpers';
import toast from 'react-hot-toast';

export const useRobotController = () => {
  const dispatch = useDispatch();
  const robotState = useSelector(selectRobot);
  const isConnected = useSelector(selectIsConnected);
  const motors = useSelector(selectMotors);
  const challengeStatus = useSelector(selectChallengeStatus);

  const { 
    isSupported, 
    connect: connectSerial, 
    disconnect: disconnectSerial, 
    sendCommand, 
    pingMotor: serialPingMotor,
    emergencyStop: serialEmergencyStop 
  } = useWebSerial();

  const [connectionStartTime, setConnectionStartTime] = useState(null);
  const [challengeInterval, setChallengeInterval] = useState(null);
  const [uptimeInterval, setUptimeInterval] = useState(null);
  const [monitoringInterval, setMonitoringInterval] = useState(null);

  const performanceRef = useRef({
    commandsSent: 0,
    commandsSuccessful: 0,
    lastPerformanceUpdate: now(),
  });

  // Connection management
  useEffect(() => {
    if (isConnected && !connectionStartTime) {
      setConnectionStartTime(now());
      startUptimeCounter();
      startPeriodicMonitoring();

      // Auto-scan for motors after connection
      setTimeout(() => {
        scanMotors();
      }, 2000);

    } else if (!isConnected && connectionStartTime) {
      setConnectionStartTime(null);
      stopUptimeCounter();
      stopPeriodicMonitoring();
      stopChallenge();
    }
  }, [isConnected, connectionStartTime]);

  // Uptime counter
  const startUptimeCounter = useCallback(() => {
    const interval = setInterval(() => {
      if (connectionStartTime) {
        const uptime = now() - connectionStartTime;
        dispatch(updateUptime(uptime));
      }
    }, 1000);
    setUptimeInterval(interval);
  }, [connectionStartTime, dispatch]);

  const stopUptimeCounter = useCallback(() => {
    if (uptimeInterval) {
      clearInterval(uptimeInterval);
      setUptimeInterval(null);
    }
  }, [uptimeInterval]);

  // Periodic monitoring
  const startPeriodicMonitoring = useCallback(() => {
    const interval = setInterval(async () => {
      if (!isConnected || motors.length === 0) return;

      try {
        // Monitor each connected motor
        for (const motor of motors) {
          if (motor.online) {
            // Get current position and temperature
            await updateMotorData(motor.id);
          }
        }

        // Update performance metrics
        updatePerformanceMetrics();

      } catch (error) {
        debugLog('Monitoring error:', error);
      }
    }, robotState.settings.updateRate || 1000);

    setMonitoringInterval(interval);
  }, [isConnected, motors, robotState.settings.updateRate]);

  const stopPeriodicMonitoring = useCallback(() => {
    if (monitoringInterval) {
      clearInterval(monitoringInterval);
      setMonitoringInterval(null);
    }
  }, [monitoringInterval]);

  // Update motor data
  const updateMotorData = useCallback(async (motorId) => {
    try {
      // This would be implemented with actual sensor reading
      // For now, we'll simulate or use cached values

      const motor = motors.find(m => m.id === motorId);
      if (!motor) return;

      // Simulate data updates (in real implementation, this would read from hardware)
      const currentTime = now();
      const timeDiff = currentTime - motor.lastUpdate;

      if (timeDiff > 100) { // Update every 100ms
        const simulatedData = {
          position: motor.position + (Math.random() - 0.5) * 2, // Small random variation
          velocity: motor.velocity + (Math.random() - 0.5) * 5,
          temperature: 25 + Math.random() * 10,
          current: Math.random() * 0.5,
        };

        dispatch(updateMotor({
          motorId,
          updates: {
            ...simulatedData,
            lastUpdate: currentTime,
          },
        }));

        // Update chart data
        dispatch(updateChartData({
          time: currentTime,
          motorId,
          position: simulatedData.position,
          velocity: simulatedData.velocity,
          temperature: simulatedData.temperature,
          current: simulatedData.current,
        }));
      }

    } catch (error) {
      errorLog(`Failed to update motor ${motorId} data`, error);
    }
  }, [motors, dispatch]);

  // Performance metrics update (throttled)
  const updatePerformanceMetrics = useCallback(
    throttle(() => {
      const now_time = now();
      const timeDiff = now_time - performanceRef.current.lastPerformanceUpdate;

      if (timeDiff > 0) {
        const commandsPerSecond = (performanceRef.current.commandsSent / timeDiff) * 1000;
        const successRate = performanceRef.current.commandsSuccessful / performanceRef.current.commandsSent;

        // Reset counters
        performanceRef.current.commandsSent = 0;
        performanceRef.current.commandsSuccessful = 0;
        performanceRef.current.lastPerformanceUpdate = now_time;

        // Dispatch performance update if needed
        debugLog('Performance metrics:', { commandsPerSecond, successRate });
      }
    }, 5000), // Update every 5 seconds
    []
  );

  // Connect to robot
  const connect = useCallback(async () => {
    try {
      dispatch(addLogEntry({
        message: 'Attempting to connect to robot...',
        type: 'info',
        timestamp: now(),
      }));

      const success = await connectSerial();

      if (success) {
        dispatch(addLogEntry({
          message: 'Successfully connected to robot',
          type: 'info',
          timestamp: now(),
        }));
        toast.success('Robot connected!');
      }

      return success;
    } catch (error) {
      errorLog('Robot connection failed', error);
      dispatch(addLogEntry({
        message: `Connection failed: ${error.message}`,
        type: 'error',
        timestamp: now(),
      }));
      toast.error('Connection failed');
      return false;
    }
  }, [connectSerial, dispatch]);

  // Disconnect from robot
  const disconnect = useCallback(async () => {
    try {
      // Stop any running challenge
      if (challengeStatus.running) {
        await stopChallenge();
      }

      await disconnectSerial();

      dispatch(addLogEntry({
        message: 'Disconnected from robot',
        type: 'info',
        timestamp: now(),
      }));

      toast.success('Robot disconnected');
    } catch (error) {
      errorLog('Disconnect error', error);
      toast.error('Disconnect error');
    }
  }, [disconnectSerial, challengeStatus.running, dispatch]);

  // Motor scanning
  const scanMotors = useCallback(async () => {
    if (!isConnected) {
      toast.error('Robot not connected');
      return;
    }

    dispatch(addLogEntry({
      message: 'Scanning for motors (ID 1-10)...',
      type: 'info',
      timestamp: now(),
    }));

    const detectedMotors = [];

    try {
      for (let id = 1; id <= 10; id++) {
        try {
          const isOnline = await serialPingMotor(id);

          if (isOnline) {
            const motor = {
              id,
              model: ROBOT_SPECS.MODEL,
              status: 'online',
              online: true,
              torqueEnabled: false,
              position: ROBOT_SPECS.MAX_POSITION / 2,
              velocity: 0,
              goalPosition: ROBOT_SPECS.MAX_POSITION / 2,
              goalVelocity: 0,
              temperature: 25,
              voltage: 7.0,
              current: 0.0,
              load: 0,
              lastUpdate: now(),
              operatingMode: 3, // Position mode
            };

            detectedMotors.push(motor);

            dispatch(addLogEntry({
              message: `âœ… Motor ${id} detected`,
              type: 'info',
              timestamp: now(),
            }));
          }

          // Small delay between pings
          await new Promise(resolve => setTimeout(resolve, 200));

        } catch (error) {
          debugLog(`Motor ${id} scan failed:`, error);
        }
      }

      dispatch(setMotors(detectedMotors));

      const message = `Scan complete. Found ${detectedMotors.length} motors.`;
      dispatch(addLogEntry({
        message,
        type: detectedMotors.length > 0 ? 'info' : 'warning',
        timestamp: now(),
      }));

      if (detectedMotors.length === 0) {
        toast.error('No motors detected. Check connections and power.');
      } else {
        toast.success(`Found ${detectedMotors.length} motors`);
      }

    } catch (error) {
      errorLog('Motor scan failed', error);
      dispatch(addLogEntry({
        message: `Motor scan failed: ${error.message}`,
        type: 'error',
        timestamp: now(),
      }));
      toast.error('Motor scan failed');
    }
  }, [isConnected, serialPingMotor, dispatch]);

  // Motor control functions
  const enableTorque = useCallback(async (motorId) => {
    try {
      performanceRef.current.commandsSent++;
      await sendCommand(`${COMMANDS.ENABLE_TORQUE}:${motorId}`);
      performanceRef.current.commandsSuccessful++;

      dispatch(updateMotor({
        motorId,
        updates: { torqueEnabled: true }
      }));

      dispatch(addLogEntry({
        message: `Torque enabled for motor ${motorId}`,
        type: 'info',
        timestamp: now(),
      }));

      dispatch(incrementCommandCount());
      return true;
    } catch (error) {
      errorLog(`Failed to enable torque for motor ${motorId}`, error);
      toast.error(`Failed to enable motor ${motorId}`);
      return false;
    }
  }, [sendCommand, dispatch]);

  const disableTorque = useCallback(async (motorId) => {
    try {
      performanceRef.current.commandsSent++;
      await sendCommand(`${COMMANDS.DISABLE_TORQUE}:${motorId}`);
      performanceRef.current.commandsSuccessful++;

      dispatch(updateMotor({
        motorId,
        updates: { torqueEnabled: false }
      }));

      dispatch(addLogEntry({
        message: `Torque disabled for motor ${motorId}`,
        type: 'info',
        timestamp: now(),
      }));

      dispatch(incrementCommandCount());
      return true;
    } catch (error) {
      errorLog(`Failed to disable torque for motor ${motorId}`, error);
      toast.error(`Failed to disable motor ${motorId}`);
      return false;
    }
  }, [sendCommand, dispatch]);

  const setMotorPosition = useCallback(async (motorId, degrees) => {
    try {
      const positionRaw = degreesToPositionRaw(degrees);
      const clampedPosition = clamp(positionRaw, 0, ROBOT_SPECS.MAX_POSITION);

      performanceRef.current.commandsSent++;
      await sendCommand(`${COMMANDS.SET_POSITION}:${motorId}:${clampedPosition}`);
      performanceRef.current.commandsSuccessful++;

      dispatch(updateMotor({
        motorId,
        updates: { 
          goalPosition: rawToDegrees(clampedPosition),
          position: rawToDegrees(clampedPosition) // Immediate update for UI responsiveness
        }
      }));

      dispatch(addLogEntry({
        message: `Motor ${motorId} position set to ${degrees.toFixed(1)}Â° (raw: ${clampedPosition})`,
        type: 'info',
        timestamp: now(),
      }));

      dispatch(incrementCommandCount());
      return true;
    } catch (error) {
      errorLog(`Failed to set position for motor ${motorId}`, error);
      toast.error(`Failed to move motor ${motorId}`);
      return false;
    }
  }, [sendCommand, dispatch]);

  const setMotorVelocity = useCallback(async (motorId, degreesPerSec) => {
    try {
      const velocityRaw = degreesToVelocityRaw(degreesPerSec);
      const clampedVelocity = clamp(velocityRaw, ROBOT_SPECS.MIN_VELOCITY, ROBOT_SPECS.MAX_VELOCITY);

      performanceRef.current.commandsSent++;
      await sendCommand(`${COMMANDS.SET_VELOCITY}:${motorId}:${clampedVelocity}`);
      performanceRef.current.commandsSuccessful++;

      dispatch(updateMotor({
        motorId,
        updates: { 
          goalVelocity: degreesPerSec,
          velocity: degreesPerSec // Immediate update
        }
      }));

      dispatch(addLogEntry({
        message: `Motor ${motorId} velocity set to ${degreesPerSec.toFixed(1)}Â°/s (raw: ${clampedVelocity})`,
        type: 'info',
        timestamp: now(),
      }));

      dispatch(incrementCommandCount());
      return true;
    } catch (error) {
      errorLog(`Failed to set velocity for motor ${motorId}`, error);
      toast.error(`Failed to set velocity for motor ${motorId}`);
      return false;
    }
  }, [sendCommand, dispatch]);

  const homeMotor = useCallback(async (motorId) => {
    const homePosition = 180; // Center position in degrees
    return await setMotorPosition(motorId, homePosition);
  }, [setMotorPosition]);

  const stopMotor = useCallback(async (motorId) => {
    return await setMotorVelocity(motorId, 0);
  }, [setMotorVelocity]);

  const getMotorPosition = useCallback(async (motorId) => {
    try {
      performanceRef.current.commandsSent++;
      await sendCommand(`${COMMANDS.GET_POSITION}:${motorId}`);
      performanceRef.current.commandsSuccessful++;

      dispatch(addLogEntry({
        message: `Reading position for motor ${motorId}`,
        type: 'info',
        timestamp: now(),
      }));

      dispatch(incrementCommandCount());
      return true;
    } catch (error) {
      errorLog(`Failed to get position for motor ${motorId}`, error);
      return false;
    }
  }, [sendCommand, dispatch]);

  // Global control functions
  const enableAllTorque = useCallback(async () => {
    const onlineMotors = motors.filter(m => m.online);
    let successCount = 0;

    for (const motor of onlineMotors) {
      const success = await enableTorque(motor.id);
      if (success) successCount++;
      await new Promise(resolve => setTimeout(resolve, 100));
    }

    if (successCount === onlineMotors.length) {
      toast.success('All motors enabled');
    } else {
      toast.warning(`${successCount}/${onlineMotors.length} motors enabled`);
    }
  }, [motors, enableTorque]);

  const disableAllTorque = useCallback(async () => {
    const onlineMotors = motors.filter(m => m.online);
    let successCount = 0;

    for (const motor of onlineMotors) {
      const success = await disableTorque(motor.id);
      if (success) successCount++;
      await new Promise(resolve => setTimeout(resolve, 100));
    }

    if (successCount === onlineMotors.length) {
      toast.success('All motors disabled');
    } else {
      toast.warning(`${successCount}/${onlineMotors.length} motors disabled`);
    }
  }, [motors, disableTorque]);

  const homeAllMotors = useCallback(async () => {
    const onlineMotors = motors.filter(m => m.online);

    dispatch(addLogEntry({
      message: 'Moving all motors to home position...',
      type: 'info',
      timestamp: now(),
    }));

    let successCount = 0;

    for (const motor of onlineMotors) {
      const success = await homeMotor(motor.id);
      if (success) successCount++;
      await new Promise(resolve => setTimeout(resolve, 100));
    }

    if (successCount === onlineMotors.length) {
      toast.success('All motors homed');
    } else {
      toast.warning(`${successCount}/${onlineMotors.length} motors homed`);
    }
  }, [motors, homeMotor, dispatch]);

  const emergencyStop = useCallback(async () => {
    try {
      dispatch(addLogEntry({
        message: 'ðŸ›‘ EMERGENCY STOP ACTIVATED',
        type: 'error',
        timestamp: now(),
      }));

      dispatch(setEmergencyStop(true));

      // Stop serial communication emergency stop
      await serialEmergencyStop();

      // Stop any running challenge
      if (challengeStatus.running) {
        stopChallenge();
      }

      toast.error('Emergency stop activated!');

    } catch (error) {
      errorLog('Emergency stop failed', error);
      toast.error('Emergency stop failed!');
    }
  }, [serialEmergencyStop, challengeStatus.running, dispatch]);

  // Challenge execution
  const startChallengeExecution = useCallback(async (challengeParams) => {
    if (!isConnected || challengeStatus.running) {
      toast.error('Cannot start challenge: Robot not ready');
      return false;
    }

    const requiredMotors = challengeParams.requiredMotors || [1, 2];
    const availableMotors = motors.filter(m => 
      requiredMotors.includes(m.id) && m.online
    );

    if (availableMotors.length < requiredMotors.length) {
      toast.error(`Challenge requires motors ${requiredMotors.join(', ')} to be connected`);
      return false;
    }

    try {
      dispatch(startChallenge(challengeParams));

      dispatch(addLogEntry({
        message: 'ðŸš€ Challenge started successfully',
        type: 'info',
        timestamp: now(),
      }));

      toast.success('Challenge started!');
      return true;

    } catch (error) {
      errorLog('Challenge start failed', error);
      toast.error('Failed to start challenge');
      return false;
    }
  }, [isConnected, challengeStatus.running, motors, dispatch]);

  const stopChallenge = useCallback(async () => {
    if (!challengeStatus.running) return;

    try {
      // Stop challenge interval if running
      if (challengeInterval) {
        clearInterval(challengeInterval);
        setChallengeInterval(null);
      }

      // Stop all motors
      const onlineMotors = motors.filter(m => m.online);
      for (const motor of onlineMotors) {
        await stopMotor(motor.id);
      }

      dispatch(stopChallengeAction());

      dispatch(addLogEntry({
        message: 'ðŸ Challenge stopped',
        type: 'info',
        timestamp: now(),
      }));

      toast.success('Challenge stopped');

    } catch (error) {
      errorLog('Challenge stop failed', error);
      toast.error('Error stopping challenge');
    }
  }, [challengeStatus.running, challengeInterval, motors, stopMotor, dispatch]);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      stopUptimeCounter();
      stopPeriodicMonitoring();
      if (challengeInterval) {
        clearInterval(challengeInterval);
      }
    };
  }, [stopUptimeCounter, stopPeriodicMonitoring, challengeInterval]);

  return {
    // Connection state
    isSupported,
    isConnected,
    connectionStatus: robotState.connectionStatus,

    // Motor state
    motors,
    connectedMotorCount: robotState.connectedMotorCount,

    // Challenge state
    challengeRunning: challengeStatus.running,
    challengeProgress: challengeStatus.progress,

    // Communication state
    logs: robotState.logs,
    commandCount: robotState.commandCount,
    uptime: robotState.uptime,

    // Control functions
    connect,
    disconnect,
    scanMotors,

    // Motor control
    enableTorque,
    disableTorque,
    setMotorPosition,
    setMotorVelocity,
    homeMotor,
    stopMotor,
    getMotorPosition,

    // Global control
    enableAllTorque,
    disableAllTorque,
    homeAllMotors,
    emergencyStop,

    // Challenge control
    startChallenge: startChallengeExecution,
    stopChallenge,
  };
};
