// src/components/challenges/Challenge1.jsx
import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import {
  Box,
  Typography,
  Button,
  Card,
  CardContent,
  Grid,
  Chip,
  LinearProgress,
  Alert,
  Paper
} from '@mui/material';
import { PlayArrow, Stop, Refresh, Target } from '@mui/icons-material';

const Challenge1 = ({ challenge, onDataChange, onError }) => {
  const dispatch = useDispatch();
  const { jointAngles, connected, loading } = useSelector(state => state.robot);
  
  const [challengeState, setChallengeState] = useState('not_started');
  const [targetAngles, setTargetAngles] = useState([0, 0, 0]);
  const [currentAngles, setCurrentAngles] = useState([0, 0, 0]);
  const [accuracy, setAccuracy] = useState(0);
  const [attempts, setAttempts] = useState(0);
  const [timeElapsed, setTimeElapsed] = useState(0);

  useEffect(() => {
    // Generate random target angles when component mounts
    generateTargetAngles();
    
    // Update current angles from robot state
    if (jointAngles && jointAngles.length >= 3) {
      setCurrentAngles(jointAngles.slice(0, 3));
    }
  }, [jointAngles]);

  useEffect(() => {
    // Calculate accuracy based on current vs target angles
    const accuracy = calculateAccuracy(currentAngles, targetAngles);
    setAccuracy(accuracy);
    
    // Update parent component with submission data
    if (onDataChange) {
      onDataChange({
        targetAngles,
        currentAngles,
        accuracy,
        attempts,
        timeElapsed
      });
    }
  }, [currentAngles, targetAngles, attempts, timeElapsed, onDataChange]);

  const generateTargetAngles = () => {
    const newTargets = [
      Math.floor(Math.random() * 180 - 90), // Joint 1: -90 to 90
      Math.floor(Math.random() * 120 - 60), // Joint 2: -60 to 60
      Math.floor(Math.random() * 120 - 60)  // Joint 3: -60 to 60
    ];
    setTargetAngles(newTargets);
  };

  const calculateAccuracy = (current, target) => {
    const totalError = current.reduce((sum, angle, index) => {
      const error = Math.abs(angle - target[index]);
      return sum + error;
    }, 0);
    
    const maxPossibleError = 180 * 3; // Maximum possible total error
    const accuracy = Math.max(0, ((maxPossibleError - totalError) / maxPossibleError) * 100);
    return Math.round(accuracy);
  };

  const startChallenge = () => {
    setChallengeState('active');
    setAttempts(0);
    setTimeElapsed(0);
    generateTargetAngles();
    
    // Start timer
    const startTime = Date.now();
    const timer = setInterval(() => {
      setTimeElapsed(Math.floor((Date.now() - startTime) / 1000));
    }, 1000);
    
    // Store timer reference for cleanup
    window.challengeTimer = timer;
  };

  const completeChallenge = () => {
    setChallengeState('completed');
    setAttempts(prev => prev + 1);
    
    // Clear timer
    if (window.challengeTimer) {
      clearInterval(window.challengeTimer);
    }
    
    if (onDataChange) {
      onDataChange({
        targetAngles,
        currentAngles,
        accuracy,
        attempts: attempts + 1,
        timeElapsed,
        completed: true
      });
    }
  };

  const resetChallenge = () => {
    setChallengeState('not_started');
    setAccuracy(0);
    setAttempts(0);
    setTimeElapsed(0);
    generateTargetAngles();
    
    if (window.challengeTimer) {
      clearInterval(window.challengeTimer);
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <Box>
      {/* Challenge Header */}
      <Paper sx={{ p: 3, mb: 3, textAlign: 'center' }}>
        <Typography variant="h5" gutterBottom>
          ð¯ Manual Robot Movement Challenge
        </Typography>
        <Typography variant="body1" color="text.secondary">
          Move the robot joints to match the target angles as closely as possible.
        </Typography>
      </Paper>

      {/* Connection Status */}
      {!connected && (
        <Alert severity="warning" sx={{ mb: 3 }}>
          Robot not connected. Challenge will run in simulation mode.
        </Alert>
      )}

      {/* Challenge Controls */}
      <Box sx={{ textAlign: 'center', mb: 3 }}>
        {challengeState === 'not_started' && (
          <Button
            variant="contained"
            size="large"
            startIcon={<PlayArrow />}
            onClick={startChallenge}
            disabled={loading}
          >
            {loading ? 'Starting...' : 'Start Challenge'}
          </Button>
        )}

        {challengeState === 'active' && (
          <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center' }}>
            <Button
              variant="contained"
              color="success"
              size="large"
              startIcon={<Stop />}
              onClick={completeChallenge}
              disabled={loading}
            >
              {loading ? 'Submitting...' : 'Complete Challenge'}
            </Button>
            
            <Button
              variant="outlined"
              startIcon={<Refresh />}
              onClick={resetChallenge}
            >
              Reset
            </Button>
          </Box>
        )}

        {challengeState === 'completed' && (
          <Button
            variant="outlined"
            startIcon={<Refresh />}
            onClick={resetChallenge}
          >
            Try Again
          </Button>
        )}
      </Box>

      {/* Progress Indicator */}
      {loading && (
        <Box sx={{ mb: 3 }}>
          <LinearProgress />
        </Box>
      )}

      {/* Challenge Content */}
      {challengeState !== 'not_started' && (
        <Grid container spacing={3}>
          {/* Target Angles */}
          <Grid item xs={12} md={6}>
            <Card>
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  ð¯ Target Angles
                </Typography>
                
                {targetAngles.map((angle, index) => (
                  <Box key={index} sx={{ mb: 2 }}>
                    <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                      <Typography variant="body2">
                        Joint {index + 1}
                      </Typography>
                      <Chip 
                        label={`${angle}Â°`} 
                        color="primary" 
                        variant="outlined"
                      />
                    </Box>
                    
                    {/* Visual angle indicator */}
                    <Box sx={{ 
                      height: 8, 
                      backgroundColor: 'rgba(255,255,255,0.1)', 
                      borderRadius: 4,
                      position: 'relative'
                    }}>
                      <Box sx={{
                        position: 'absolute',
                        left: `${((angle + 90) / 180) * 100}%`,
                        top: 0,
                        width: 4,
                        height: '100%',
                        backgroundColor: 'primary.main',
                        borderRadius: 4
                      }} />
                    </Box>
                  </Box>
                ))}
              </CardContent>
            </Card>
          </Grid>

          {/* Current Angles */}
          <Grid item xs={12} md={6}>
            <Card>
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  ð Current Angles
                </Typography>
                
                {currentAngles.map((angle, index) => (
                  <Box key={index} sx={{ mb: 2 }}>
                    <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                      <Typography variant="body2">
                        Joint {index + 1}
                      </Typography>
                      <Chip 
                        label={`${Math.round(angle)}Â°`} 
                        color="secondary" 
                        variant="outlined"
                      />
                    </Box>
                    
                    {/* Visual angle indicator */}
                    <Box sx={{ 
                      height: 8, 
                      backgroundColor: 'rgba(255,255,255,0.1)', 
                      borderRadius: 4,
                      position: 'relative'
                    }}>
                      <Box sx={{
                        position: 'absolute',
                        left: `${((angle + 90) / 180) * 100}%`,
                        top: 0,
                        width: 4,
                        height: '100%',
                        backgroundColor: 'secondary.main',
                        borderRadius: 4
                      }} />
                    </Box>
                    
                    {/* Error indicator */}
                    <Typography variant="caption" color="text.secondary">
                      Error: {Math.abs(angle - targetAngles[index]).toFixed(1)}Â°
                    </Typography>
                  </Box>
                ))}
              </CardContent>
            </Card>
          </Grid>

          {/* Statistics */}
          <Grid item xs={12}>
            <Card>
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  ð Performance Metrics
                </Typography>
                
                <Grid container spacing={3}>
                  <Grid item xs={6} sm={3}>
                    <Box sx={{ textAlign: 'center' }}>
                      <Typography variant="h4" color="primary" sx={{ fontWeight: 'bold' }}>
                        {accuracy}%
                      </Typography>
                      <Typography variant="caption" color="text.secondary">
                        Accuracy
                      </Typography>
                    </Box>
                  </Grid>
                  
                  <Grid item xs={6} sm={3}>
                    <Box sx={{ textAlign: 'center' }}>
                      <Typography variant="h4" color="secondary" sx={{ fontWeight: 'bold' }}>
                        {attempts}
                      </Typography>
                      <Typography variant="caption" color="text.secondary">
                        Attempts
                      </Typography>
                    </Box>
                  </Grid>
                  
                  <Grid item xs={6} sm={3}>
                    <Box sx={{ textAlign: 'center' }}>
                      <Typography variant="h4" color="info.main" sx={{ fontWeight: 'bold' }}>
                        {formatTime(timeElapsed)}
                      </Typography>
                      <Typography variant="caption" color="text.secondary">
                        Time
                      </Typography>
                    </Box>
                  </Grid>
                  
                  <Grid item xs={6} sm={3}>
                    <Box sx={{ textAlign: 'center' }}>
                      <Typography 
                        variant="h4" 
                        sx={{ 
                          fontWeight: 'bold',
                          color: accuracy > 80 ? 'success.main' : accuracy > 60 ? 'warning.main' : 'error.main'
                        }}
                      >
                        {accuracy > 80 ? 'A' : accuracy > 60 ? 'B' : 'C'}
                      </Typography>
                      <Typography variant="caption" color="text.secondary">
                        Grade
                      </Typography>
                    </Box>
                  </Grid>
                </Grid>
              </CardContent>
            </Card>
          </Grid>
        </Grid>
      )}
    </Box>
  );
};

export default Challenge1;
