import { useState, useEffect, useCallback } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { updateRobotState, setConnectionStatus } from '../store/robotSlice';
import { useWebSerial } from './useWebSerial';

// Robot controller hook that uses WebSerial for communication
export const useRobotController = () => {
  const dispatch = useDispatch();
  const { connected, connectionStatus } = useSelector(state => state.robot);

  const [currentAngles, setCurrentAngles] = useState([0, 0, 0]);
  const [isSimulationMode, setIsSimulationMode] = useState(true);

  // Use the full WebSerial implementation
  const {
    isSupported,
    isConnected: serialConnected,
    connect: connectSerial,
    disconnect: disconnectSerial,
    sendCommand,
    pingMotor,
    getMotorPosition,
    emergencyStop: serialEmergencyStop
  } = useWebSerial();

  // Connection status - use serial connection if available, otherwise simulation
  const isConnected = serialConnected || false;

  // Initialize connection attempt
  useEffect(() => {
    const initializeConnection = async () => {
      if (isSupported) {
        try {
          dispatch(setConnectionStatus('connecting'));
          console.log('Attempting to connect to robot hardware...');

          // Try to connect (this will show browser serial port dialog)
          // await connectSerial();

          // For now, default to simulation mode
          dispatch(setConnectionStatus('disconnected'));
          setIsSimulationMode(true);
          console.log('Robot controller initialized in simulation mode');

        } catch (error) {
          console.error('Failed to connect to robot:', error);
          dispatch(setConnectionStatus('error'));
          setIsSimulationMode(true);
        }
      } else {
        console.log('WebSerial not supported, using simulation mode');
        dispatch(setConnectionStatus('disconnected'));
        setIsSimulationMode(true);
      }
    };

    initializeConnection();
  }, [dispatch, isSupported, connectSerial]);

  // Manual connect function for user-initiated connection
  const connectToRobot = useCallback(async () => {
    if (!isSupported) {
      throw new Error('WebSerial not supported in this browser');
    }

    try {
      await connectSerial();
      setIsSimulationMode(false);
      return true;
    } catch (error) {
      console.error('Failed to connect to robot:', error);
      setIsSimulationMode(true);
      throw error;
    }
  }, [isSupported, connectSerial]);

  // Disconnect function
  const disconnectFromRobot = useCallback(async () => {
    try {
      await disconnectSerial();
      setIsSimulationMode(true);
    } catch (error) {
      console.error('Error disconnecting:', error);
      throw error;
    }
  }, [disconnectSerial]);

  // Robot control functions
  const disableTorque = useCallback(async () => {
    try {
      if (serialConnected) {
        // Send disable torque command for all motors
        await sendCommand('DISABLE:1');
        await sendCommand('DISABLE:2');
        await sendCommand('DISABLE:3');
        console.log('Torque disabled for all motors');
      } else {
        console.log('Disabling torque (simulation)');
      }
      return Promise.resolve();
    } catch (error) {
      console.error('Error disabling torque:', error);
      throw error;
    }
  }, [serialConnected, sendCommand]);

  const enableTorque = useCallback(async () => {
    try {
      if (serialConnected) {
        // Send enable torque command for all motors
        await sendCommand('ENABLE:1');
        await sendCommand('ENABLE:2');
        await sendCommand('ENABLE:3');
        console.log('Torque enabled for all motors');
      } else {
        console.log('Enabling torque (simulation)');
      }
      return Promise.resolve();
    } catch (error) {
      console.error('Error enabling torque:', error);
      throw error;
    }
  }, [serialConnected, sendCommand]);

  const getCurrentJointAngles = useCallback(async () => {
    try {
      if (serialConnected) {
        // Get positions from all motors
        const positions = await Promise.all([
          getMotorPosition(1),
          getMotorPosition(2),
          getMotorPosition(3)
        ]);

        // Convert raw positions to angles in radians
        const angles = positions.map(pos => {
          if (pos === null) return 0;
          // Convert raw position (0-4095) to degrees, then to radians
          const degrees = ((pos - 2048) / 2048) * 180; // Assuming center is 2048
          return degrees * Math.PI / 180;
        });

        setCurrentAngles(angles.map(rad => rad * 180 / Math.PI)); // Store in degrees
        return angles;
      } else {
        // Simulation mode - return mock angles with some variation
        const angles = currentAngles.map(angle => 
          angle + (Math.random() - 0.5) * 10 // ±5 degree variation
        );
        setCurrentAngles(angles);
        return angles.map(angle => angle * Math.PI / 180); // Convert to radians
      }
    } catch (error) {
      console.error('Error getting joint angles:', error);
      return [0, 0, 0];
    }
  }, [serialConnected, getMotorPosition, currentAngles]);

  const getRelativeJointAngles = useCallback(async () => {
    try {
      if (serialConnected) {
        // For relative angles, we need to calculate the difference between adjacent joints
        const absoluteAngles = await getCurrentJointAngles();

        // Convert absolute angles to relative angles
        const relativeAngles = [
          absoluteAngles[0], // First joint is relative to base
          absoluteAngles[1] - absoluteAngles[0], // Second relative to first
          absoluteAngles[2] - absoluteAngles[1]  // Third relative to second
        ];

        return relativeAngles;
      } else {
        // Simulation mode - return mock relative angles
        return currentAngles.map(angle => angle * Math.PI / 180);
      }
    } catch (error) {
      console.error('Error getting relative joint angles:', error);
      return [0, 0, 0];
    }
  }, [serialConnected, getCurrentJointAngles, currentAngles]);

  const moveToPosition = useCallback(async (angles) => {
    try {
      if (serialConnected) {
        // Convert angles to raw positions and send commands
        const rawPositions = angles.map(angle => {
          const degrees = angle * 180 / Math.PI; // Convert to degrees
          return Math.round(2048 + (degrees / 180) * 2048); // Convert to raw position
        });

        await Promise.all([
          sendCommand(`SET_POS:1:${rawPositions[0]}`),
          sendCommand(`SET_POS:2:${rawPositions[1]}`),
          sendCommand(`SET_POS:3:${rawPositions[2]}`)
        ]);

        console.log('Moved to position:', angles);
      } else {
        // Simulation mode
        console.log('Moving to position (simulation):', angles);
        setCurrentAngles(angles.map(rad => rad * 180 / Math.PI));
      }

      return Promise.resolve();
    } catch (error) {
      console.error('Error moving to position:', error);
      throw error;
    }
  }, [serialConnected, sendCommand]);

  const setVelocity = useCallback(async (motorId, velocity) => {
    try {
      if (serialConnected) {
        await sendCommand(`SET_VEL:${motorId}:${velocity}`);
        console.log(`Set velocity for motor ${motorId}: ${velocity}`);
      } else {
        console.log(`Setting velocity for motor ${motorId}: ${velocity} (simulation)`);
      }
      return Promise.resolve();
    } catch (error) {
      console.error('Error setting velocity:', error);
      throw error;
    }
  }, [serialConnected, sendCommand]);

  const emergencyStop = useCallback(async () => {
    try {
      if (serialConnected) {
        await serialEmergencyStop();
      } else {
        console.log('Emergency stop activated (simulation)');
        setCurrentAngles([0, 0, 0]); // Reset to safe position
      }
      return Promise.resolve();
    } catch (error) {
      console.error('Error during emergency stop:', error);
      throw error;
    }
  }, [serialConnected, serialEmergencyStop]);

  const calibrate = useCallback(async () => {
    try {
      if (serialConnected) {
        // Send calibration commands
        await sendCommand('CALIBRATE');
        console.log('Robot calibration started');
      } else {
        console.log('Calibrating robot (simulation)');
        setCurrentAngles([0, 0, 0]); // Reset to home position
      }
      return Promise.resolve();
    } catch (error) {
      console.error('Error during calibration:', error);
      throw error;
    }
  }, [serialConnected, sendCommand]);

  const startChallenge = useCallback(async (challengeId) => {
    try {
      console.log(`Starting challenge ${challengeId}${serialConnected ? '' : ' (simulation)'}`);
      dispatch(updateRobotState({ 
        status: 'challenge_active',
        currentChallenge: challengeId 
      }));
      return Promise.resolve();
    } catch (error) {
      console.error('Error starting challenge:', error);
      throw error;
    }
  }, [serialConnected, dispatch]);

  const stopChallenge = useCallback(async () => {
    try {
      console.log(`Stopping challenge${serialConnected ? '' : ' (simulation)'}`);
      dispatch(updateRobotState({ 
        status: 'idle',
        currentChallenge: null 
      }));
      return Promise.resolve();
    } catch (error) {
      console.error('Error stopping challenge:', error);
      throw error;
    }
  }, [serialConnected, dispatch]);

  return {
    // Connection status
    isConnected: serialConnected,
    connectionStatus,
    isSimulationMode,
    isSupported,

    // Current state
    currentAngles,

    // Connection management
    connectToRobot,
    disconnectFromRobot,

    // Control functions
    disableTorque,
    enableTorque,
    getCurrentJointAngles,
    getRelativeJointAngles,
    moveToPosition,
    setVelocity,
    emergencyStop,
    calibrate,

    // Challenge functions
    startChallenge,
    stopChallenge,

    // Hardware functions
    pingMotor,
    getMotorPosition,

    // Robot specs for compatibility
    robotSpecs: {
      linkLengths: [0.10, 0.07, 0.05], // meters
      jointLimits: [
        { min: -180, max: 180 },
        { min: -90, max: 90 },
        { min: -90, max: 90 }
      ],
      maxVelocity: 50, // degrees/second
      precision: 0.1 // degrees
    }
  };
};

export default useRobotController;
