// src/components/challenges/RobotVisualizer.jsx
import React, { useRef, useEffect, useState } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls, Text, Box, Cylinder } from '@react-three/drei';
import { useSelector } from 'react-redux';
import { Paper, Typography, Switch, FormControlLabel, Box as MuiBox } from '@mui/material';
import * as THREE from 'three';

// Robot Arm Component
const RobotArm = ({ jointAngles = [0, 0, 0] }) => {
  const baseRef = useRef();
  const shoulder1Ref = useRef();
  const shoulder2Ref = useRef();
  const elbowRef = useRef();
  
  useFrame(() => {
    // Update joint rotations based on angles
    if (baseRef.current) {
      baseRef.current.rotation.y = THREE.MathUtils.degToRad(jointAngles[0] || 0);
    }
    if (shoulder1Ref.current) {
      shoulder1Ref.current.rotation.z = THREE.MathUtils.degToRad(jointAngles[1] || 0);
    }
    if (elbowRef.current) {
      elbowRef.current.rotation.z = THREE.MathUtils.degToRad(jointAngles[2] || 0);
    }
  });

  return (
    <group>
      {/* Base */}
      <group ref={baseRef}>
        <Cylinder args={[0.3, 0.3, 0.2]} position={[0, 0.1, 0]}>
          <meshStandardMaterial color="#666666" />
        </Cylinder>
        
        {/* First Link */}
        <group ref={shoulder1Ref}>
          <Cylinder args={[0.1, 0.1, 1]} position={[0, 0.7, 0]}>
            <meshStandardMaterial color="#0cc0df" />
          </Cylinder>
          
          {/* Second Link */}
          <group position={[0, 1.2, 0]} ref={shoulder2Ref}>
            <Cylinder args={[0.08, 0.08, 0.8]} position={[0, 0.4, 0]}>
              <meshStandardMaterial color="#007acc" />
            </Cylinder>
            
            {/* Third Link (End Effector) */}
            <group position={[0, 0.8, 0]} ref={elbowRef}>
              <Cylinder args={[0.06, 0.06, 0.5]} position={[0, 0.25, 0]}>
                <meshStandardMaterial color="#FF6B35" />
              </Cylinder>
              
              {/* End Effector */}
              <Box args={[0.1, 0.1, 0.1]} position={[0, 0.55, 0]}>
                <meshStandardMaterial color="#FF0000" />
              </Box>
            </group>
          </group>
        </group>
      </group>
      
      {/* Coordinate System */}
      <group>
        {/* X Axis - Red */}
        <Cylinder args={[0.02, 0.02, 2]} position={[1, 0, 0]} rotation={[0, 0, Math.PI / 2]}>
          <meshStandardMaterial color="#FF0000" />
        </Cylinder>
        
        {/* Y Axis - Green */}
        <Cylinder args={[0.02, 0.02, 2]} position={[0, 1, 0]}>
          <meshStandardMaterial color="#00FF00" />
        </Cylinder>
        
        {/* Z Axis - Blue */}
        <Cylinder args={[0.02, 0.02, 2]} position={[0, 0, 1]} rotation={[Math.PI / 2, 0, 0]}>
          <meshStandardMaterial color="#0000FF" />
        </Cylinder>
      </group>
      
      {/* Grid */}
      <gridHelper args={[10, 20, "#444444", "#222222"]} />
    </group>
  );
};

// Trajectory Path Component
const TrajectoryPath = ({ points = [] }) => {
  if (points.length < 2) return null;
  
  const pathGeometry = new THREE.BufferGeometry().setFromPoints(
    points.map(p => new THREE.Vector3(p.x, p.y, p.z))
  );
  
  return (
    <line>
      <bufferGeometry attach="geometry" {...pathGeometry} />
      <lineBasicMaterial attach="material" color="#FFD700" linewidth={3} />
    </line>
  );
};

const RobotVisualizer = () => {
  const { jointAngles, connected } = useSelector(state => state.robot);
  const [showTrajectory, setShowTrajectory] = useState(false);
  const [showCoordinates, setShowCoordinates] = useState(true);
  const [trajectoryPoints, setTrajectoryPoints] = useState([]);

  useEffect(() => {
    // Add current end effector position to trajectory
    if (showTrajectory && jointAngles) {
      // Calculate end effector position using forward kinematics
      const endPos = calculateEndEffectorPosition(jointAngles);
      setTrajectoryPoints(prev => [...prev.slice(-50), endPos]); // Keep last 50 points
    }
  }, [jointAngles, showTrajectory]);

  const calculateEndEffectorPosition = (angles) => {
    // Simple forward kinematics calculation
    const [a1, a2, a3] = angles.map(a => THREE.MathUtils.degToRad(a));
    
    const l1 = 1.0; // Length of first link
    const l2 = 0.8; // Length of second link
    const l3 = 0.5; // Length of third link
    
    const x = (l1 * Math.cos(a2) + l2 * Math.cos(a2 + a3)) * Math.cos(a1);
    const y = l1 * Math.sin(a2) + l2 * Math.sin(a2 + a3) + 0.2; // Add base height
    const z = (l1 * Math.cos(a2) + l2 * Math.cos(a2 + a3)) * Math.sin(a1);
    
    return { x, y, z };
  };

  return (
    <Paper sx={{ p: 2, height: 500 }}>
      {/* Controls */}
      <MuiBox sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
        <Typography variant="h6">
          Robot Visualization
        </Typography>
        
        <MuiBox sx={{ display: 'flex', gap: 2 }}>
          <FormControlLabel
            control={
              <Switch
                checked={showTrajectory}
                onChange={(e) => setShowTrajectory(e.target.checked)}
              />
            }
            label="Show Trajectory"
          />
          
          <FormControlLabel
            control={
              <Switch
                checked={showCoordinates}
                onChange={(e) => setShowCoordinates(e.target.checked)}
              />
            }
            label="Show Coordinates"
          />
        </MuiBox>
      </MuiBox>

      {/* 3D Canvas */}
      <Canvas
        camera={{ position: [3, 3, 3], fov: 75 }}
        style={{ height: '100%', background: '#000814' }}
      >
        {/* Lighting */}
        <ambientLight intensity={0.4} />
        <directionalLight position={[5, 5, 5]} intensity={0.8} />
        <directionalLight position={[-5, 5, -5]} intensity={0.3} />
        
        {/* Robot */}
        <RobotArm jointAngles={jointAngles || [0, 0, 0]} />
        
        {/* Trajectory */}
        {showTrajectory && (
          <TrajectoryPath points={trajectoryPoints} />
        )}
        
        {/* Coordinate Labels */}
        {showCoordinates && (
          <group>
            <Text position={[2.2, 0, 0]} fontSize={0.2} color="#FF0000">
              X
            </Text>
            <Text position={[0, 2.2, 0]} fontSize={0.2} color="#00FF00">
              Y
            </Text>
            <Text position={[0, 0, 2.2]} fontSize={0.2} color="#0000FF">
              Z
            </Text>
          </group>
        )}
        
        {/* Controls */}
        <OrbitControls 
          enablePan={true}
          enableZoom={true}
          enableRotate={true}
          maxDistance={10}
          minDistance={1}
        />
      </Canvas>
      
      {/* Status */}
      <MuiBox sx={{ mt: 1, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Typography variant="caption" color="text.secondary">
          {connected ? 'Connected to robot' : 'Simulation mode'}
        </Typography>
        
        {jointAngles && (
          <Typography variant="caption" color="text.secondary">
            Angles: [{jointAngles.map(a => a.toFixed(1)).join(', ')}]Â°
          </Typography>
        )}
      </MuiBox>
    </Paper>
  );
};

export default RobotVisualizer;
