// Utility helper functions
export const getRank = (challengesCompleted, totalScore) => {
  if (challengesCompleted >= 15) return 'Robotics Master';
  if (challengesCompleted >= 12) return 'Expert Engineer';
  if (challengesCompleted >= 8) return 'Advanced Student';
  if (challengesCompleted >= 5) return 'Intermediate Learner';
  if (challengesCompleted >= 2) return 'Beginner Student';
  return 'New Student';
};

export const getScoreColor = (score) => {
  if (score >= 90) return '#4CAF50'; // Green
  if (score >= 75) return '#2196F3'; // Blue
  if (score >= 60) return '#FF9800'; // Orange
  return '#F44336'; // Red
};

export const calculateProgress = (completed, total) => {
  if (total === 0) return 0;
  return Math.round((completed / total) * 100);
};

export const getDifficultyColor = (difficulty) => {
  switch (difficulty?.toLowerCase()) {
    case 'beginner': return '#4CAF50';
    case 'intermediate': return '#FF9800';
    case 'advanced': return '#F44336';
    case 'expert': return '#9C27B0';
    default: return '#2196F3';
  }
};

export const formatDate = (dateString) => {
  return new Date(dateString).toLocaleDateString();
};

export const formatDuration = (seconds) => {
  const minutes = Math.floor(seconds / 60);
  const remainingSeconds = seconds % 60;
  return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
};

export const calculateAccuracy = (attempts, successes) => {
  if (attempts === 0) return 0;
  return Math.round((successes / attempts) * 100);
};

export const clampAngle = (angle, min = -Math.PI, max = Math.PI) => {
  return Math.max(min, Math.min(max, angle));
};

export const degreesToRadians = (degrees) => {
  return degrees * (Math.PI / 180);
};

export const radiansToDegrees = (radians) => {
  return radians * (180 / Math.PI);
};

export const generateRandomAngles = () => {
  return [
    Math.random() * Math.PI - Math.PI/2,
    Math.random() * Math.PI - Math.PI/2,
    Math.random() * Math.PI - Math.PI/2
  ];
};

export const debounce = (func, wait) => {
  let timeout;
  return function executedFunction(...args) {
    const later = () => {
      clearTimeout(timeout);
      func(...args);
    };
    clearTimeout(timeout);
    timeout = setTimeout(later, wait);
  };
};

export const throttle = (func, limit) => {
  let inThrottle;
  return function() {
    const args = arguments;
    const context = this;
    if (!inThrottle) {
      func.apply(context, args);
      inThrottle = true;
      setTimeout(() => inThrottle = false, limit);
    }
  };
};
