// =================== LoginForm.jsx ===================
import React, { useState, useEffect } from 'react';
import { 
  Box, 
  TextField, 
  Button, 
  Typography, 
  Alert, 
  Paper,
  Tab,
  Tabs,
  InputAdornment,
  IconButton
} from '@mui/material';
import { Visibility, VisibilityOff, Person, Lock } from '@mui/icons-material';
import { useAuth } from '../../hooks/useAuth';
import { validateUsername, validatePassword } from '../../utils/validation';
import LoadingSpinner from '../common/LoadingSpinner';

const LoginForm = () => {
  const { login, register, loading, error, clearAuthError } = useAuth();
  const [tab, setTab] = useState(0); // 0 = login, 1 = register
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    confirmPassword: ''
  });
  const [formErrors, setFormErrors] = useState({});
  const [showPassword, setShowPassword] = useState(false);

  useEffect(() => {
    // Clear errors when switching tabs
    clearAuthError();
    setFormErrors({});
  }, [tab, clearAuthError]);

  const handleTabChange = (event, newValue) => {
    setTab(newValue);
    setFormData({ username: '', password: '', confirmPassword: '' });
    setFormErrors({});
  };

  const handleInputChange = (field) => (event) => {
    const value = event.target.value;
    setFormData(prev => ({ ...prev, [field]: value }));

    // Clear field-specific error when user starts typing
    if (formErrors[field]) {
      setFormErrors(prev => ({ ...prev, [field]: '' }));
    }
  };

  const validateForm = () => {
    const errors = {};

    // Username validation
    const usernameError = validateUsername(formData.username);
    if (usernameError) errors.username = usernameError;

    // Password validation
    const passwordError = validatePassword(formData.password);
    if (passwordError) errors.password = passwordError;

    // Confirm password validation (only for register)
    if (tab === 1) {
      if (formData.password !== formData.confirmPassword) {
        errors.confirmPassword = 'Passwords do not match';
      }
    }

    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!validateForm()) {
      return;
    }

    const success = tab === 0 
      ? await login(formData.username, formData.password)
      : await register(formData.username, formData.password);

    if (success && tab === 1) {
      // Auto-switch to login tab after successful registration
      setTab(0);
      setFormData({ username: formData.username, password: '', confirmPassword: '' });
    }
  };

  if (loading) {
    return <LoadingSpinner message="Authenticating..." />;
  }

  return (
    <Box sx={{ maxWidth: 400, mx: 'auto', mt: 4 }}>
      <Paper elevation={3} sx={{ p: 4, bgcolor: 'background.paper' }}>
        {/* Tabs */}
        <Tabs value={tab} onChange={handleTabChange} centered sx={{ mb: 3 }}>
          <Tab label="Login" />
          <Tab label="Register" />
        </Tabs>

        {/* Form */}
        <Box component="form" onSubmit={handleSubmit}>
          {/* Error Alert */}
          {error && (
            <Alert severity="error" sx={{ mb: 2 }}>
              {error}
            </Alert>
          )}

          {/* Username Field */}
          <TextField
            fullWidth
            label="Username"
            value={formData.username}
            onChange={handleInputChange('username')}
            error={!!formErrors.username}
            helperText={formErrors.username}
            margin="normal"
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <Person />
                </InputAdornment>
              ),
            }}
            autoComplete="username"
          />

          {/* Password Field */}
          <TextField
            fullWidth
            label="Password"
            type={showPassword ? 'text' : 'password'}
            value={formData.password}
            onChange={handleInputChange('password')}
            error={!!formErrors.password}
            helperText={formErrors.password}
            margin="normal"
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <Lock />
                </InputAdornment>
              ),
              endAdornment: (
                <InputAdornment position="end">
                  <IconButton
                    onClick={() => setShowPassword(!showPassword)}
                    edge="end"
                  >
                    {showPassword ? <VisibilityOff /> : <Visibility />}
                  </IconButton>
                </InputAdornment>
              ),
            }}
            autoComplete={tab === 0 ? 'current-password' : 'new-password'}
          />

          {/* Confirm Password Field (Register only) */}
          {tab === 1 && (
            <TextField
              fullWidth
              label="Confirm Password"
              type={showPassword ? 'text' : 'password'}
              value={formData.confirmPassword}
              onChange={handleInputChange('confirmPassword')}
              error={!!formErrors.confirmPassword}
              helperText={formErrors.confirmPassword}
              margin="normal"
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <Lock />
                  </InputAdornment>
                ),
              }}
              autoComplete="new-password"
            />
          )}

          {/* Submit Button */}
          <Button
            type="submit"
            fullWidth
            variant="contained"
            size="large"
            disabled={loading}
            sx={{ mt: 3, mb: 2, py: 1.5 }}
          >
            {tab === 0 ? 'Login' : 'Create Account'}
          </Button>

          {/* Help Text */}
          <Typography variant="body2" color="text.secondary" align="center">
            {tab === 0 
              ? "Don't have an account? Click the Register tab above."
              : "Already have an account? Click the Login tab above."
            }
          </Typography>
        </Box>
      </Paper>
    </Box>
  );
};

export default LoginForm;
