// src/components/challenges/ChallengeCard.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Card,
  CardContent,
  CardActions,
  Typography,
  Chip,
  Box,
  IconButton,
  Menu,
  MenuItem,
  LinearProgress
} from '@mui/material';
import {
  PlayArrow,
  Lock,
  CheckCircle,
  MoreVert,
  History,
  Replay,
  Star,
  Timer
} from '@mui/icons-material';

const ChallengeCard = ({ challenge, onHistoryClick }) => {
  const navigate = useNavigate();
  const [anchorEl, setAnchorEl] = useState(null);
  const open = Boolean(anchorEl);

  const handleClick = () => {
    if (challenge.is_unlocked && !challenge.is_locked) {
      navigate(`/challenge/${challenge.id}`);
    }
  };

  const handleMenuClick = (event) => {
    event.stopPropagation();
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const handleHistoryClick = () => {
    onHistoryClick(challenge.id);
    handleMenuClose();
  };

  const getDifficultyColor = (difficulty) => {
    switch (difficulty?.toLowerCase()) {
      case 'beginner': return '#4CAF50';
      case 'intermediate': return '#FF9800';
      case 'advanced': return '#F44336';
      case 'expert': return '#9C27B0';
      default: return '#757575';
    }
  };

  const getStatusInfo = () => {
    if (challenge.is_locked || !challenge.is_unlocked) {
      return {
        icon: <Lock />,
        text: 'Locked',
        color: '#757575'
      };
    } else if (challenge.is_completed) {
      return {
        icon: <CheckCircle />,
        text: 'Completed',
        color: '#4CAF50'
      };
    } else {
      return {
        icon: <PlayArrow />,
        text: 'Start',
        color: '#0cc0df'
      };
    }
  };

  const statusInfo = getStatusInfo();
  const isLocked = challenge.is_locked || !challenge.is_unlocked;

  return (
    <Card
      sx={{
        height: '100%',
        display: 'flex',
        flexDirection: 'column',
        cursor: isLocked ? 'not-allowed' : 'pointer',
        transition: 'all 0.3s ease',
        background: challenge.is_completed 
          ? 'linear-gradient(145deg, #1b4332, #2d5a41)'
          : isLocked 
          ? 'linear-gradient(145deg, #424242, #616161)'
          : 'linear-gradient(145deg, #1a1a1a, #2a2a2a)',
        border: '1px solid',
        borderColor: challenge.is_completed 
          ? '#4CAF50' 
          : isLocked 
          ? '#757575' 
          : '#333',
        opacity: isLocked ? 0.7 : 1,
        '&:hover': {
          transform: isLocked ? 'none' : 'translateY(-5px)',
          boxShadow: isLocked ? 'none' : '0 15px 35px rgba(12, 192, 223, 0.2)',
          borderColor: isLocked ? '#757575' : '#0cc0df'
        }
      }}
      onClick={handleClick}
    >
      <CardContent sx={{ flexGrow: 1, position: 'relative', pb: 1 }}>
        {/* Difficulty Badge */}
        <Chip
          label={challenge.difficulty || 'Beginner'}
          size="small"
          sx={{
            position: 'absolute',
            top: 16,
            right: 16,
            backgroundColor: getDifficultyColor(challenge.difficulty),
            color: 'white',
            fontWeight: 'bold',
            fontSize: '0.7rem'
          }}
        />

        {/* Challenge Title */}
        <Typography
          variant="h6"
          component="h3"
          gutterBottom
          sx={{
            fontWeight: 'bold',
            color: isLocked ? '#999' : 'white',
            pr: 8 // Space for difficulty badge
          }}
        >
          Challenge {challenge.id}: {challenge.title}
        </Typography>

        {/* Description */}
        <Typography
          variant="body2"
          color="text.secondary"
          sx={{ mb: 2, lineHeight: 1.5 }}
        >
          {challenge.description}
        </Typography>

        {/* Score Display */}
        {challenge.is_completed && challenge.best_score !== undefined && (
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 1 }}>
            <Star sx={{ color: '#FFD700', fontSize: '1rem' }} />
            <Typography variant="body2" sx={{ fontWeight: 'bold', color: '#FFD700' }}>
              Best Score: {challenge.best_score}%
            </Typography>
          </Box>
        )}

        {/* Estimated Time */}
        {challenge.estimated_time && (
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 1 }}>
            <Timer sx={{ color: '#757575', fontSize: '1rem' }} />
            <Typography variant="body2" color="text.secondary">
              ~{challenge.estimated_time} min
            </Typography>
          </Box>
        )}

        {/* Progress Bar for partially completed */}
        {challenge.progress && challenge.progress > 0 && challenge.progress < 100 && (
          <Box sx={{ mt: 2 }}>
            <Typography variant="caption" color="text.secondary">
              Progress: {challenge.progress}%
            </Typography>
            <LinearProgress 
              variant="determinate" 
              value={challenge.progress} 
              sx={{ mt: 0.5, height: 6, borderRadius: 3 }}
            />
          </Box>
        )}
      </CardContent>

      <CardActions sx={{ justifyContent: 'space-between', pt: 0 }}>
        {/* Status Indicator */}
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
          <Box sx={{ color: statusInfo.color, display: 'flex', alignItems: 'center' }}>
            {statusInfo.icon}
          </Box>
          <Typography variant="caption" sx={{ color: statusInfo.color, fontWeight: 'bold' }}>
            {statusInfo.text}
          </Typography>
        </Box>

        {/* Menu Button */}
        <IconButton
          size="small"
          onClick={handleMenuClick}
          sx={{ color: 'text.secondary' }}
        >
          <MoreVert />
        </IconButton>
      </CardActions>

      {/* Context Menu */}
      <Menu
        anchorEl={anchorEl}
        open={open}
        onClose={handleMenuClose}
        onClick={(e) => e.stopPropagation()}
      >
        <MenuItem onClick={handleHistoryClick}>
          <History sx={{ mr: 1 }} />
          View History
        </MenuItem>
        {challenge.is_completed && (
          <MenuItem onClick={() => { navigate(`/challenge/${challenge.id}`); handleMenuClose(); }}>
            <Replay sx={{ mr: 1 }} />
            Retry Challenge
          </MenuItem>
        )}
      </Menu>
    </Card>
  );
};

export default ChallengeCard;
