// src/pages/DashboardPage.jsx
import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { motion } from 'framer-motion';
import DashboardHeader from '../components/dashboard/DashboardHeader';
import ChallengeGrid from '../components/dashboard/ChallengeGrid';
import ProgressTracker from '../components/dashboard/ProgressTracker';
import RobotStatus from '../components/dashboard/RobotStatus';
import { useChallenges } from '../hooks/useChallenges';
import { useRobotController } from '../hooks/useRobotController';
import '../styles/dashboard.css';

const DashboardPage = () => {
  const dispatch = useDispatch();
  const { user } = useSelector(state => state.auth);
  const { challenges, progress, loading } = useSelector(state => state.challenges);
  const { robotStatus } = useSelector(state => state.robot);

  const { fetchChallenges, fetchProgress } = useChallenges();
  const { connectRobot } = useRobotController();

  useEffect(() => {
    fetchChallenges();
    fetchProgress();
    connectRobot();
  }, []);

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        duration: 0.5,
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: { duration: 0.5 }
    }
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="loading-spinner">
          <i className="fas fa-robot spinning"></i>
          <p>Initializing Aurora Rising...</p>
        </div>
      </div>
    );
  }

  return (
    <motion.div 
      className="dashboard-container"
      variants={containerVariants}
      initial="hidden"
      animate="visible"
    >
      {/* Animated Background */}
      <div className="background-animation">
        {Array.from({ length: 30 }, (_, i) => (
          <div
            key={i}
            className="particle"
            style={{
              left: `${Math.random() * 100}%`,
              animationDelay: `${Math.random() * 20}s`,
              animationDuration: `${Math.random() * 10 + 15}s`
            }}
          />
        ))}
      </div>

      {/* Header */}
      <motion.div variants={itemVariants}>
        <DashboardHeader user={user} progress={progress} />
      </motion.div>

      {/* Main Content */}
      <div className="dashboard-content">
        {/* Dashboard Title */}
        <motion.div className="dashboard-title" variants={itemVariants}>
          <h1 className="title-main">Robotics Summer School</h1>
          <p className="subtitle">Master robot control through hands-on challenges</p>
        </motion.div>

        {/* Progress Overview */}
        <motion.div variants={itemVariants}>
          <ProgressTracker progress={progress} challenges={challenges} />
        </motion.div>

        {/* Main Grid Layout */}
        <div className="dashboard-grid">
          {/* Challenges Grid */}
          <motion.div className="challenges-section" variants={itemVariants}>
            <ChallengeGrid challenges={challenges} progress={progress} />
          </motion.div>

          {/* Robot Status Sidebar */}
          <motion.div className="robot-section" variants={itemVariants}>
            <RobotStatus robotStatus={robotStatus} />
          </motion.div>
        </div>
      </div>
    </motion.div>
  );
};

export default DashboardPage;
