// Loading component for PersistGate
const PersistGateLoading = () => (
  <Box 
    display="flex" 
    justifyContent="center" 
    alignItems="center" 
    minHeight="100vh"
    bgcolor="background.default"
  >
    <LoadingSpinner />
  </Box>
);

// Main App component
function App() {
  // Handle screen resize for responsive design
  useEffect(() => {
    const handleResize = () => {
      // You can dispatch screen size updates to Redux if needed
      console.log('Screen resized:', window.innerWidth, window.innerHeight);
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <Provider store={store}>
      <PersistGate loading={<PersistGateLoading />} persistor={persistor}>
        <ThemeProvider theme={theme}>
          <CssBaseline />
          <ErrorProvider>
            <ErrorBoundary>
              <Router>
                <Layout>
                  <Suspense fallback={<LoadingSpinner />}>
                    <Routes>
                      {/* Public routes */}
                      <Route path="/" element={<HomePage />} />
                      <Route path="/login" element={<HomePage />} />
                      <Route path="/register" element={<HomePage />} />
                      
                      {/* Protected routes */}
                      <Route 
                        path="/dashboard" 
                        element={
                          <AuthGuard>
                            <DashboardPage />
                          </AuthGuard>
                        } 
                      />
                      <Route 
                        path="/challenges" 
                        element={
                          <AuthGuard>
                            <ChallengePage />
                          </AuthGuard>
                        } 
                      />
                      <Route 
                        path="/challenges/:challengeId" 
                        element={
                          <AuthGuard>
                            <IndividualChallengePage />
                          </AuthGuard>
                        } 
                      />
                      <Route 
                        path="/leaderboard" 
                        element={
                          <AuthGuard>
                            <LeaderboardPage />
                          </AuthGuard>
                        } 
                      />
                      <Route 
                        path="/profile" 
                        element={
                          <AuthGuard>
                            <ProfilePage />
                          </AuthGuard>
                        } 
                      />
                      
                      {/* Fallback route */}
                      <Route path="*" element={<Navigate to="/" replace />} />
                    </Routes>
                  </Suspense>
                </Layout>
              </Router>
            </ErrorBoundary>
          </ErrorProvider>
        </ThemeProvider>
      </PersistGate>
    </Provider>
  );
}

export default App;
