// src/store/robotSlice.js
import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import robotService from '../services/robotService';

// Async thunks for robot operations
export const connectRobot = createAsyncThunk(
  'robot/connect',
  async (_, { rejectWithValue }) => {
    try {
      console.log('ðŸ¤– Robot slice: Connecting to robot...');
      const response = await robotService.connect();
      return response.data;
    } catch (error) {
      console.error('ðŸ¤– Robot slice: Connect error:', error);
      return rejectWithValue(error.response?.data?.detail || error.message);
    }
  }
);

export const disconnectRobot = createAsyncThunk(
  'robot/disconnect',
  async (_, { rejectWithValue }) => {
    try {
      console.log('ðŸ¤– Robot slice: Disconnecting from robot...');
      const response = await robotService.disconnect();
      return response.data;
    } catch (error) {
      console.error('ðŸ¤– Robot slice: Disconnect error:', error);
      return rejectWithValue(error.response?.data?.detail || error.message);
    }
  }
);

export const fetchRobotStatus = createAsyncThunk(
  'robot/fetchStatus',
  async (_, { rejectWithValue }) => {
    try {
      const response = await robotService.getStatus();
      return response.data;
    } catch (error) {
      console.error('ðŸ¤– Robot slice: Fetch status error:', error);
      return rejectWithValue(error.response?.data?.detail || error.message);
    }
  }
);

export const moveJoint = createAsyncThunk(
  'robot/moveJoint',
  async ({ jointId, angle, speed }, { rejectWithValue }) => {
    try {
      console.log(`ðŸ¤– Robot slice: Moving joint ${jointId} to ${angle}Â°...`);
      const response = await robotService.moveJoint(jointId, angle, speed);
      return response.data;
    } catch (error) {
      console.error(`ðŸ¤– Robot slice: Move joint ${jointId} error:`, error);
      return rejectWithValue(error.response?.data?.detail || error.message);
    }
  }
);

export const moveToPosition = createAsyncThunk(
  'robot/moveToPosition',
  async ({ angles, speed }, { rejectWithValue }) => {
    try {
      console.log('ðŸ¤– Robot slice: Moving to position:', angles);
      const response = await robotService.moveToPosition(angles, speed);
      return response.data;
    } catch (error) {
      console.error('ðŸ¤– Robot slice: Move to position error:', error);
      return rejectWithValue(error.response?.data?.detail || error.message);
    }
  }
);

export const emergencyStop = createAsyncThunk(
  'robot/emergencyStop',
  async (_, { rejectWithValue }) => {
    try {
      console.log('ðŸš¨ Robot slice: Emergency stop!');
      const response = await robotService.emergencyStop();
      return response.data;
    } catch (error) {
      console.error('ðŸš¨ Robot slice: Emergency stop error:', error);
      return rejectWithValue(error.response?.data?.detail || error.message);
    }
  }
);

export const homePosition = createAsyncThunk(
  'robot/homePosition',
  async (_, { rejectWithValue }) => {
    try {
      console.log('ðŸ  Robot slice: Moving to home position...');
      const response = await robotService.homePosition();
      return response.data;
    } catch (error) {
      console.error('ðŸ  Robot slice: Home position error:', error);
      return rejectWithValue(error.response?.data?.detail || error.message);
    }
  }
);

// Default actuator structure
const createDefaultActuator = (id) => ({
  id: id,
  enabled: false,
  position: 0,
  velocity: 0,
  current: 0,
  temperature: 25.0,
  voltage: 0,
  torque_enabled: false,
  moving: false,
  hardware_error: 0,
  goal_position: 0,
  present_position: 0,
  goal_velocity: 0,
  present_velocity: 0,
  goal_current: 0,
  present_current: 0,
  present_temperature: 25.0,
  present_voltage: 0,
  registered_instruction: 0,
  status_return_level: 2,
  hardware_error_status: 0
});

const createDefaultActuators = () => ({
  1: createDefaultActuator(1),
  2: createDefaultActuator(2),
  3: createDefaultActuator(3)
});

const initialState = {
  // Connection status
  connected: false,
  connectionStatus: 'disconnected', // 'disconnected', 'connecting', 'connected', 'error'

  // Robot state
  jointAngles: [0.0, 0.0, 0.0],
  jointPositions: [0.0, 0.0, 0.0],
  jointVelocities: [0.0, 0.0, 0.0],
  actuators: createDefaultActuators(),

  // Safety and status
  safetyStatus: {
    safe: true,
    emergencyStop: false,
    warnings: []
  },
  bridgeStatus: {
    connected: false,
    version: null,
    last_heartbeat: null,
    active_bridges: []
  },
  hardwareErrors: [],

  // UI state
  loading: false,
  error: null,
  lastUpdate: null,

  // Control state
  isMoving: false,
  currentCommand: null,
  commandQueue: [],
};

const robotSlice = createSlice({
  name: 'robot',
  initialState,
  reducers: {
    setConnectionStatus: (state, action) => {
      const status = action.payload;
      state.connectionStatus = status;
      state.connected = status === 'connected';

      if (status === 'disconnected') {
        // Reset robot state on disconnect
        state.jointAngles = [0.0, 0.0, 0.0];
        state.jointPositions = [0.0, 0.0, 0.0];
        state.jointVelocities = [0.0, 0.0, 0.0];
        state.actuators = createDefaultActuators();
        state.isMoving = false;
        state.currentCommand = null;
      }

      console.log('ðŸ¤– Robot slice: Connection status changed to:', status);
    },

    updateRobotState: (state, action) => {
      const data = action.payload;

      // Update joint data if provided
      if (data.joint_angles && Array.isArray(data.joint_angles)) {
        state.jointAngles = data.joint_angles.slice(0, 3);
      }

      if (data.joint_positions && Array.isArray(data.joint_positions)) {
        state.jointPositions = data.joint_positions.slice(0, 3);
      }

      if (data.joint_velocities && Array.isArray(data.joint_velocities)) {
        state.jointVelocities = data.joint_velocities.slice(0, 3);
      }

      // Update actuator data
      if (data.actuators) {
        Object.entries(data.actuators).forEach(([id, actuatorData]) => {
          if (state.actuators[id]) {
            state.actuators[id] = {
              ...state.actuators[id],
              ...actuatorData
            };
          }
        });
      }

      // Update safety status
      if (data.safety_status) {
        state.safetyStatus = {
          ...state.safetyStatus,
          ...data.safety_status
        };
      }

      // Update bridge status
      if (data.bridge_status) {
        state.bridgeStatus = {
          ...state.bridgeStatus,
          ...data.bridge_status
        };
      }

      // Update movement status
      if (data.is_moving !== undefined) {
        state.isMoving = data.is_moving;
      }

      // Update hardware errors
      if (data.hardware_errors && Array.isArray(data.hardware_errors)) {
        state.hardwareErrors = data.hardware_errors;
      }

      state.lastUpdate = new Date().toISOString();
    },

    setRobotError: (state, action) => {
      state.error = action.payload;
      console.error('ðŸ¤– Robot slice: Error set:', action.payload);
    },

    clearRobotError: (state) => {
      state.error = null;
    },

    setLoading: (state, action) => {
      state.loading = action.payload;
    },

    resetRobotState: (state) => {
      console.log('ðŸ”„ Robot slice: Resetting robot state');
      return {
        ...initialState,
        connectionStatus: state.connectionStatus,
        connected: state.connected
      };
    },

    updateJointAngle: (state, action) => {
      const { jointIndex, angle } = action.payload;
      if (jointIndex >= 0 && jointIndex < state.jointAngles.length) {
        state.jointAngles[jointIndex] = angle;
        state.lastUpdate = new Date().toISOString();
      }
    },

    setCurrentCommand: (state, action) => {
      state.currentCommand = action.payload;
    },

    addToCommandQueue: (state, action) => {
      state.commandQueue.push(action.payload);
    },

    removeFromCommandQueue: (state, action) => {
      const commandId = action.payload;
      state.commandQueue = state.commandQueue.filter(cmd => cmd.id !== commandId);
    },

    clearCommandQueue: (state) => {
      state.commandQueue = [];
      state.currentCommand = null;
    },
  },

  extraReducers: (builder) => {
    builder
      // Connect robot cases
      .addCase(connectRobot.pending, (state) => {
        state.loading = true;
        state.connectionStatus = 'connecting';
        state.error = null;
      })
      .addCase(connectRobot.fulfilled, (state, action) => {
        state.loading = false;
        state.connected = true;
        state.connectionStatus = 'connected';
        state.error = null;

        if (action.payload.robot_status) {
          robotSlice.caseReducers.updateRobotState(state, { payload: action.payload.robot_status });
        }

        console.log('âœ… Robot slice: Connected successfully');
      })
      .addCase(connectRobot.rejected, (state, action) => {
        state.loading = false;
        state.connected = false;
        state.connectionStatus = 'error';
        state.error = action.payload || 'Connection failed';
      })

      // Disconnect robot cases
      .addCase(disconnectRobot.pending, (state) => {
        state.loading = true;
      })
      .addCase(disconnectRobot.fulfilled, (state) => {
        state.loading = false;
        state.connected = false;
        state.connectionStatus = 'disconnected';
        state.error = null;

        // Reset robot state
        state.jointAngles = [0.0, 0.0, 0.0];
        state.jointPositions = [0.0, 0.0, 0.0];
        state.jointVelocities = [0.0, 0.0, 0.0];
        state.actuators = createDefaultActuators();
        state.isMoving = false;
        state.currentCommand = null;
        state.commandQueue = [];

        console.log('âœ… Robot slice: Disconnected successfully');
      })
      .addCase(disconnectRobot.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload || 'Disconnection failed';
      })

      // Fetch status cases
      .addCase(fetchRobotStatus.fulfilled, (state, action) => {
        robotSlice.caseReducers.updateRobotState(state, { payload: action.payload });
      })
      .addCase(fetchRobotStatus.rejected, (state, action) => {
        state.error = action.payload || 'Status fetch failed';
      })

      // Move joint cases
      .addCase(moveJoint.pending, (state, action) => {
        state.loading = true;
        state.isMoving = true;
        state.error = null;
        state.currentCommand = {
          type: 'move_joint',
          jointId: action.meta.arg.jointId,
          angle: action.meta.arg.angle,
          startTime: new Date().toISOString()
        };
      })
      .addCase(moveJoint.fulfilled, (state, action) => {
        state.loading = false;

        if (action.payload.robot_status) {
          robotSlice.caseReducers.updateRobotState(state, { payload: action.payload.robot_status });
        }

        // Update specific joint angle if provided
        if (action.payload.joint_angles) {
          state.jointAngles = action.payload.joint_angles;
        }
      })
      .addCase(moveJoint.rejected, (state, action) => {
        state.loading = false;
        state.isMoving = false;
        state.currentCommand = null;
        state.error = action.payload || 'Joint movement failed';
      })

      // Move to position cases
      .addCase(moveToPosition.pending, (state, action) => {
        state.loading = true;
        state.isMoving = true;
        state.error = null;
        state.currentCommand = {
          type: 'move_to_position',
          angles: action.meta.arg.angles,
          startTime: new Date().toISOString()
        };
      })
      .addCase(moveToPosition.fulfilled, (state, action) => {
        state.loading = false;

        if (action.payload.robot_status) {
          robotSlice.caseReducers.updateRobotState(state, { payload: action.payload.robot_status });
        }

        if (action.payload.joint_angles) {
          state.jointAngles = action.payload.joint_angles;
        }
      })
      .addCase(moveToPosition.rejected, (state, action) => {
        state.loading = false;
        state.isMoving = false;
        state.currentCommand = null;
        state.error = action.payload || 'Position movement failed';
      })

      // Emergency stop cases
      .addCase(emergencyStop.fulfilled, (state) => {
        state.isMoving = false;
        state.currentCommand = null;
        state.commandQueue = [];
        state.safetyStatus.emergencyStop = true;

        console.log('ðŸš¨ Robot slice: Emergency stop activated');
      })
      .addCase(emergencyStop.rejected, (state, action) => {
        state.error = action.payload || 'Emergency stop failed';
      })

      // Home position cases
      .addCase(homePosition.pending, (state) => {
        state.loading = true;
        state.isMoving = true;
        state.error = null;
        state.currentCommand = {
          type: 'home_position',
          startTime: new Date().toISOString()
        };
      })
      .addCase(homePosition.fulfilled, (state, action) => {
        state.loading = false;

        if (action.payload.robot_status) {
          robotSlice.caseReducers.updateRobotState(state, { payload: action.payload.robot_status });
        }

        // Reset to home position
        state.jointAngles = [0.0, 0.0, 0.0];
        state.jointPositions = [0.0, 0.0, 0.0];
        state.jointVelocities = [0.0, 0.0, 0.0];
      })
      .addCase(homePosition.rejected, (state, action) => {
        state.loading = false;
        state.isMoving = false;
        state.currentCommand = null;
        state.error = action.payload || 'Home position failed';
      });
  },
});

