// src/components/challenges/ChallengeMap.jsx
import React, { useState } from 'react';
import { Grid, Box, Typography, Container } from '@mui/material';
import ChallengeCard from './ChallengeCard';
import ChallengeHistoryDialogue from './ChallengeHistoryDialogue';

const ChallengeMap = ({ challenges }) => {
  const [historyDialogOpen, setHistoryDialogOpen] = useState(false);
  const [selectedChallengeId, setSelectedChallengeId] = useState(null);

  const handleHistoryClick = (challengeId) => {
    setSelectedChallengeId(challengeId);
    setHistoryDialogOpen(true);
  };

  const handleHistoryClose = () => {
    setHistoryDialogOpen(false);
    setSelectedChallengeId(null);
  };

  if (!challenges || challenges.length === 0) {
    return (
      <Container maxWidth="md">
        <Box className="text-center" sx={{ py: 8 }}>
          <Typography variant="h5" color="text.secondary" gutterBottom>
            No challenges available
          </Typography>
          <Typography variant="body1" color="text.secondary">
            Challenges will appear here once they are loaded from the server
          </Typography>
        </Box>
      </Container>
    );
  }

  // Group challenges by difficulty for better organization
  const groupedChallenges = challenges.reduce((groups, challenge) => {
    const difficulty = challenge.difficulty || 'Beginner';
    if (!groups[difficulty]) {
      groups[difficulty] = [];
    }
    groups[difficulty].push(challenge);
    return groups;
  }, {});

  const difficultyOrder = ['Beginner', 'Intermediate', 'Advanced', 'Expert'];

  return (
    <Box>
      {difficultyOrder.map(difficulty => {
        const challengesInGroup = groupedChallenges[difficulty];
        if (!challengesInGroup || challengesInGroup.length === 0) return null;

        return (
          <Box key={difficulty} sx={{ mb: 6 }}>
            <Typography 
              variant="h5" 
              gutterBottom 
              sx={{ 
                fontWeight: 'bold', 
                color: 'primary.main',
                mb: 3,
                textAlign: 'center'
              }}
            >
              {difficulty} Challenges
            </Typography>
            
            <Grid container spacing={3} justifyContent="center">
              {challengesInGroup.map((challenge) => (
                <Grid item key={challenge.id} xs={12} sm={6} md={4} lg={3}>
                  <ChallengeCard 
                    challenge={challenge} 
                    onHistoryClick={handleHistoryClick}
                  />
                </Grid>
              ))}
            </Grid>
          </Box>
        );
      })}

      {/* History Dialog */}
      <ChallengeHistoryDialogue
        open={historyDialogOpen}
        onClose={handleHistoryClose}
        challengeId={selectedChallengeId}
      />
    </Box>
  );
};

export default ChallengeMap;
