// src/store/challengeSlice.js
import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import challengeService from '../services/challengeService';

// Async thunks for challenges
export const fetchChallenges = createAsyncThunk(
  'challenges/fetchChallenges',
  async (_, { rejectWithValue }) => {
    try {
      console.log('ðŸŽ¯ Challenge slice: Fetching challenges...');
      const response = await challengeService.getChallenges();
      return response.data;
    } catch (error) {
      console.error('ðŸŽ¯ Challenge slice: Fetch challenges error:', error);
      return rejectWithValue(error.response?.data?.detail || error.message);
    }
  }
);

export const fetchChallenge = createAsyncThunk(
  'challenges/fetchChallenge',
  async (challengeId, { rejectWithValue }) => {
    try {
      console.log(`ðŸŽ¯ Challenge slice: Fetching challenge ${challengeId}...`);
      const response = await challengeService.getChallenge(challengeId);
      return { challengeId, data: response.data };
    } catch (error) {
      console.error(`ðŸŽ¯ Challenge slice: Fetch challenge ${challengeId} error:`, error);
      return rejectWithValue(error.response?.data?.detail || error.message);
    }
  }
);

export const startChallenge = createAsyncThunk(
  'challenges/startChallenge',
  async ({ challengeId, parameters }, { rejectWithValue }) => {
    try {
      console.log(`ðŸŽ¯ Challenge slice: Starting challenge ${challengeId}...`);
      const response = await challengeService.startChallenge(challengeId, parameters);
      return { challengeId, data: response.data };
    } catch (error) {
      console.error(`ðŸŽ¯ Challenge slice: Start challenge ${challengeId} error:`, error);
      return rejectWithValue(error.response?.data?.detail || error.message);
    }
  }
);

export const submitChallenge = createAsyncThunk(
  'challenges/submitChallenge',
  async ({ challengeId, submission }, { rejectWithValue }) => {
    try {
      console.log(`ðŸŽ¯ Challenge slice: Submitting challenge ${challengeId}...`);
      const response = await challengeService.submitChallenge(challengeId, submission);
      return { challengeId, data: response.data };
    } catch (error) {
      console.error(`ðŸŽ¯ Challenge slice: Submit challenge ${challengeId} error:`, error);
      return rejectWithValue(error.response?.data?.detail || error.message);
    }
  }
);

export const fetchUserProgress = createAsyncThunk(
  'challenges/fetchUserProgress',
  async (_, { rejectWithValue }) => {
    try {
      console.log('ðŸ‘¤ Challenge slice: Fetching user progress...');
      const response = await challengeService.getUserProgress();
      return response.data;
    } catch (error) {
      console.error('ðŸ‘¤ Challenge slice: Fetch user progress error:', error);
      return rejectWithValue(error.response?.data?.detail || error.message);
    }
  }
);

export const fetchLeaderboard = createAsyncThunk(
  'challenges/fetchLeaderboard',
  async (limit = 50, { rejectWithValue }) => {
    try {
      console.log('ðŸ† Challenge slice: Fetching leaderboard...');
      const response = await challengeService.getLeaderboard(limit);
      return response.data;
    } catch (error) {
      console.error('ðŸ† Challenge slice: Fetch leaderboard error:', error);
      return rejectWithValue(error.response?.data?.detail || error.message);
    }
  }
);

export const fetchChallengeHistory = createAsyncThunk(
  'challenges/fetchChallengeHistory',
  async (challengeId, { rejectWithValue }) => {
    try {
      console.log(`ðŸ“Š Challenge slice: Fetching history for challenge ${challengeId}...`);
      const response = await challengeService.getChallengeHistory(challengeId);
      return { challengeId, data: response.data };
    } catch (error) {
      console.error(`ðŸ“Š Challenge slice: Fetch challenge ${challengeId} history error:`, error);
      return rejectWithValue(error.response?.data?.detail || error.message);
    }
  }
);

const initialState = {
  challenges: [],
  currentChallenge: null,
  currentChallengeId: null,
  challengeHistory: {},
  userProgress: {
    challengesCompleted: 0,
    totalScore: 0,
    averageScore: 0,
    rank: 'Rookie',
    recentActivity: []
  },
  leaderboard: [],
  hardwareStatus: {
    connected: false,
    available: false,
    lastCheck: null
  },
  loading: false,
  error: null,
  lastUpdate: null,
};

const challengeSlice = createSlice({
  name: 'challenges',
  initialState,
  reducers: {
    clearError: (state) => {
      state.error = null;
    },

    setCurrentChallenge: (state, action) => {
      state.currentChallengeId = action.payload;
      state.currentChallenge = state.challenges.find(c => c.id === action.payload) || null;
    },

    updateChallengeProgress: (state, action) => {
      const { challengeId, progress } = action.payload;
      const challenge = state.challenges.find(c => c.id === challengeId);
      if (challenge) {
        challenge.progress = progress;
        challenge.lastUpdate = new Date().toISOString();
      }
    },

    updateChallengeScore: (state, action) => {
      const { challengeId, score, completed } = action.payload;
      const challenge = state.challenges.find(c => c.id === challengeId);
      if (challenge) {
        if (!challenge.best_score || score > challenge.best_score) {
          challenge.best_score = score;
        }
        challenge.is_completed = completed || challenge.is_completed;
        challenge.attempts = (challenge.attempts || 0) + 1;
      }
    },

    resetChallengeState: (state) => {
      state.currentChallenge = null;
      state.currentChallengeId = null;
      state.error = null;
    },

    updateHardwareStatus: (state, action) => {
      state.hardwareStatus = {
        ...state.hardwareStatus,
        ...action.payload,
        lastCheck: new Date().toISOString()
      };
    },
  },

  extraReducers: (builder) => {
    builder
      // Fetch challenges cases
      .addCase(fetchChallenges.pending, (state) => {
        state.loading = true;
        state.error = null;
      })
      .addCase(fetchChallenges.fulfilled, (state, action) => {
        state.loading = false;
        state.challenges = action.payload.challenges || [];
        state.userProgress = {
          ...state.userProgress,
          ...action.payload.user_progress
        };
        state.lastUpdate = new Date().toISOString();

        console.log('âœ… Challenge slice: Challenges loaded:', state.challenges.length);
      })
      .addCase(fetchChallenges.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
        console.error('âŒ Challenge slice: Fetch challenges failed');
      })

      // Fetch single challenge cases
      .addCase(fetchChallenge.pending, (state) => {
        state.loading = true;
        state.error = null;
      })
      .addCase(fetchChallenge.fulfilled, (state, action) => {
        state.loading = false;
        state.currentChallenge = action.payload.data.challenge;
        state.currentChallengeId = action.payload.challengeId;

        // Update challenge in list if it exists
        const existingIndex = state.challenges.findIndex(c => c.id === action.payload.challengeId);
        if (existingIndex >= 0) {
          state.challenges[existingIndex] = action.payload.data.challenge;
        }
      })
      .addCase(fetchChallenge.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      })

      // Start challenge cases
      .addCase(startChallenge.pending, (state) => {
        state.loading = true;
        state.error = null;
      })
      .addCase(startChallenge.fulfilled, (state, action) => {
        state.loading = false;

        if (state.currentChallenge) {
          state.currentChallenge.session_id = action.payload.data.session_id;
          state.currentChallenge.status = 'in_progress';
          state.currentChallenge.startTime = new Date().toISOString();
        }

        console.log(`âœ… Challenge slice: Challenge ${action.payload.challengeId} started`);
      })
      .addCase(startChallenge.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      })

      // Submit challenge cases
      .addCase(submitChallenge.pending, (state) => {
        state.loading = true;
        state.error = null;
      })
      .addCase(submitChallenge.fulfilled, (state, action) => {
        state.loading = false;

        const { challengeId, data } = action.payload;

        // Update current challenge
        if (state.currentChallenge && state.currentChallenge.id === challengeId) {
          state.currentChallenge.status = 'completed';
          state.currentChallenge.lastResult = data;
          state.currentChallenge.completedAt = new Date().toISOString();
        }

        // Update challenge in list
        const challenge = state.challenges.find(c => c.id === challengeId);
        if (challenge) {
          challenge.is_completed = data.success || false;
          challenge.best_score = Math.max(challenge.best_score || 0, data.score || 0);
          challenge.attempts = (challenge.attempts || 0) + 1;
          challenge.last_attempt = new Date().toISOString();
        }

        // Update user progress
        if (data.success) {
          state.userProgress.challengesCompleted = state.challenges.filter(c => c.is_completed).length;
          state.userProgress.totalScore = state.challenges.reduce((sum, c) => sum + (c.best_score || 0), 0);
          state.userProgress.averageScore = state.userProgress.challengesCompleted > 0 
            ? Math.round(state.userProgress.totalScore / state.userProgress.challengesCompleted) 
            : 0;
        }

        console.log(`âœ… Challenge slice: Challenge ${challengeId} submitted successfully`);
      })
      .addCase(submitChallenge.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      })

      // Fetch user progress cases
      .addCase(fetchUserProgress.fulfilled, (state, action) => {
        state.userProgress = {
          ...state.userProgress,
          ...action.payload
        };
      })

      // Fetch leaderboard cases
      .addCase(fetchLeaderboard.pending, (state) => {
        state.loading = true;
      })
      .addCase(fetchLeaderboard.fulfilled, (state, action) => {
        state.loading = false;
        state.leaderboard = action.payload.leaderboard || [];
      })
      .addCase(fetchLeaderboard.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      })

      // Fetch challenge history cases
      .addCase(fetchChallengeHistory.fulfilled, (state, action) => {
        const { challengeId, data } = action.payload;
        state.challengeHistory[challengeId] = data.history || [];
      });
  },
});

export const {
  clearError,
  setCurrentChallenge,
  updateChallengeProgress,
  updateChallengeScore,
  resetChallengeState,
  updateHardwareStatus
} = challengeSlice.actions;

export default challengeSlice.reducer;
