import React from 'react';
import { Routes, Route } from 'react-router-dom';
import ProtectedRoute from './components/auth/ProtectedRoute';
import Header from './components/common/Header';
import Footer from './components/common/Footer';
import ErrorBoundary from './components/common/ErrorBoundary';
import HomePage from './pages/HomePage';
import DashboardPage from './pages/DashboardPage';
import IndividualChallengePage from './pages/IndividualChallengePage';
import LeaderboardPage from
 './pages/LeaderboardPage';
import ProfilePage from './pages/ProfilePage';
import { useWebSerial } from './hooks/useWebSerial';
import './styles/globals.css';

// WebSocket integration component
const WebSerialProvider = ({ children }) => {
  useWebS(); // Initialize WebSocket connection
  return children;
};

function App() {
  return (
    <ErrorBoundary>
      <WebSocketProvider>
        <div className="App">
          <Header />
          <main className="main-content">
            <Routes>
              <Route path="/" element={<HomePage />} />
              <Route 
                path="/dashboard" 
                element={
                  <ProtectedRoute>
                    <DashboardPage />
                  </ProtectedRoute>
                } 
              />
              
              {/* Updated routing to match your ChallengeGrid pattern */}
              <Route 
                path="/challenges/:id" 
                element={
                  <ProtectedRoute>
                    <IndividualChallengePage />
                  </ProtectedRoute>
                } 
              />
              
              {/* Keep backward compatibility with existing route */}
              <Route 
                path="/challenge/:id" 
                element={
                  <ProtectedRoute>
                    <IndividualChallengePage />
                  </ProtectedRoute>
                } 
              />
              
              <Route 
                path="/leaderboard" 
                element={
                  <ProtectedRoute>
                    <LeaderboardPage />
                  </ProtectedRoute>
                } 
              />
              <Route 
                path="/profile" 
                element={
                  <ProtectedRoute>
                    <ProfilePage />
                  </ProtectedRoute>
                } 
              />
            </Routes>
          </main>
          <Footer />
        </div>
      </WebSocketProvider>
    </ErrorBoundary>
  );
}

export default App;
