// =================== AuthGuard.jsx ===================
import React, { useEffect } from 'react';
import { useAuth } from '../../hooks/useAuth';
import { useNavigate, useLocation } from 'react-router-dom';

const AuthGuard = ({ children, requireAuth = true }) => {
  const { isAuthenticated, user, loading, isTokenValid } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    console.log('üõ°Ô∏è AuthGuard: Checking requirements...');
    console.log('Require auth:', requireAuth);
    console.log('Is authenticated:', isAuthenticated);
    console.log('Has user:', !!user);
    console.log('Token valid:', isTokenValid());
    console.log('Loading:', loading);

    if (!loading) {
      if (requireAuth && (!isAuthenticated || !user || !isTokenValid())) {
        console.log('üõ°Ô∏è AuthGuard: Authentication required but user not authenticated');
        navigate('/', { state: { from: location } });
      } else if (!requireAuth && isAuthenticated && user) {
        console.log('üõ°Ô∏è AuthGuard: User already authenticated, redirecting to dashboard');
        navigate('/dashboard');
      }
    }
  }, [isAuthenticated, user, loading, requireAuth, navigate, location, isTokenValid]);

  // Don't render anything while loading or during redirects
  if (loading) {
    return (
      <div style={{ 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        height: '100vh' 
      }}>
        <div>Loading...</div>
      </div>
    );
  }

  // Render children if auth requirements are met
  if (requireAuth && isAuthenticated && user && isTokenValid()) {
    return children;
  } else if (!requireAuth && (!isAuthenticated || !user)) {
    return children;
  }

  // Show loading while redirecting
  return (
    <div style={{ 
      display: 'flex', 
      justifyContent: 'center', 
      alignItems: 'center', 
      height: '100vh' 
    }}>
      <div>Redirecting...</div>
    </div>
  );
};

export default AuthGuard;
