// Loading component for Suspense
const SuspenseLoader = () => (
  <Box
    display="flex"
    justifyContent="center"
    alignItems="center"
    minHeight="60vh"
  >
    <CircularProgress size={60} thickness={4} />
  </Box>
);

// Global notification handler
const GlobalNotifications = () => {
  const { errors, warnings, removeError, removeWarning } = useError();
  const [openNotifications, setOpenNotifications] = React.useState([]);

  useEffect(() => {
    // Handle new errors
    if (errors.length > 0) {
      const latestError = errors[errors.length - 1];
      setOpenNotifications(prev => [...prev, {
        id: latestError.id,
        type: 'error',
        message: latestError.message,
        autoHideDuration: 6000
      }]);
    }
  }, [errors]);

  useEffect(() => {
    // Handle new warnings
    if (warnings.length > 0) {
      const latestWarning = warnings[warnings.length - 1];
      setOpenNotifications(prev => [...prev, {
        id: latestWarning.id,
        type: 'warning',
        message: latestWarning.message,
        autoHideDuration: 4000
      }]);
    }
  }, [warnings]);

  const handleCloseNotification = (notificationId) => {
    setOpenNotifications(prev => 
      prev.filter(notification => notification.id !== notificationId)
    );
    
    // Remove from error/warning stores
    removeError(notificationId);
    removeWarning(notificationId);
  };

  return (
    <>
      {openNotifications.map(notification => (
        <Snackbar
          key={notification.id}
          open={true}
          autoHideDuration={notification.autoHideDuration}
          onClose={() => handleCloseNotification(notification.id)}
          anchorOrigin={{ vertical: 'top', horizontal: 'right' }}
          sx={{ mt: 8 }}
        >
          <Alert
            onClose={() => handleCloseNotification(notification.id)}
            severity={notification.type}
            variant="filled"
            sx={{ width: '100%' }}
          >
            {notification.message}
          </Alert>
        </Snackbar>
      ))}
    </>
  );
};

// Main App Layout
const AppLayout = ({ children }) => {
  return (
    <Box
      sx={{
        display: 'flex',
        flexDirection: 'column',
        minHeight: '100vh',
        backgroundColor: 'background.default',
      }}
    >
      <Header />
      <Box
        component="main"
        sx={{
          flexGrow: 1,
          display: 'flex',
          flexDirection: 'column',
          pt: { xs: 8, sm: 9 }, // Account for header height
        }}
      >
        {children}
      </Box>
      <Footer />
      <GlobalNotifications />
    </Box>
  );
};

// Route configuration
const AppRoutes = () => {
  return (
    <Routes>
      {/* Public routes */}
      <Route path="/" element={<HomePage />} />
      <Route path="/login" element={<LoginForm />} />
      <Route path="/register" element={<RegisterForm />} />
      
      {/* Protected routes */}
      <Route
        path="/dashboard"
        element={
          <AuthGuard>
            <DashboardPage />
          </AuthGuard>
        }
      />
      
      <Route
        path="/challenges"
        element={
          <AuthGuard>
            <ChallengePage />
          </AuthGuard>
        }
      />
      
      <Route
        path="/challenges/:challengeId"
        element={
          <AuthGuard>
            <IndividualChallengePage />
          </AuthGuard>
        }
      />
      
      <Route
        path="/leaderboard"
        element={
          <AuthGuard>
            <LeaderboardPage />
          </AuthGuard>
        }
      />
      
      <Route
        path="/profile"
        element={
          <AuthGuard>
            <ProfilePage />
          </AuthGuard>
        }
      />
      
      {/* Fallback route */}
      <Route path="*" element={<Navigate to="/" replace />} />
    </Routes>
  );
};

// Performance monitoring setup
const usePerformanceMonitoring = () => {
  useEffect(() => {
    // Initialize performance monitoring
    performanceMonitor.recordMetric('app_start', {
      timestamp: Date.now(),
      userAgent: navigator.userAgent,
      viewport: {
        width: window.innerWidth,
        height: window.innerHeight
      }
    });

    // Monitor navigation performance
    if (performance.navigation) {
      performanceMonitor.recordMetric('navigation', {
        type: performance.navigation.type,
        redirectCount: performance.navigation.redirectCount
      });
    }

    // Monitor page visibility changes
    const handleVisibilityChange = () => {
      performanceMonitor.recordMetric('visibility_change', {
        visible: !document.hidden,
        timestamp: Date.now()
      });
    };

    document.addEventListener('visibilitychange', handleVisibilityChange);

    return () => {
      document.removeEventListener('visibilitychange', handleVisibilityChange);
    };
  }, []);
};

// Service Worker registration
const useServiceWorker = () => {
  useEffect(() => {
    if ('serviceWorker' in navigator && process.env.NODE_ENV === 'production') {
      window.addEventListener('load', () => {
        navigator.serviceWorker.register('/sw.js')
          .then(registration => {
            console.log('SW registered: ', registration);
          })
          .catch(registrationError => {
            console.log('SW registration failed: ', registrationError);
          });
      });
    }
  }, []);
};

// Main App Component
const App = () => {
  // Setup global error handling
  useEffect(() => {
    setupGlobalErrorHandling();
  }, []);

  // Custom hooks
  usePerformanceMonitoring();
  useServiceWorker();

  return (
    <ErrorBoundary>
      <Provider store={store}>
        <PersistGate 
          loading={<SuspenseLoader />} 
          persistor={persistor}
        >
          <ThemeProvider theme={theme}>
            <LocalizationProvider dateAdapter={AdapterDateFns}>
              <CssBaseline />
              <ErrorProvider>
                <Router>
                  <AppLayout>
                    <Suspense fallback={<SuspenseLoader />}>
                      <AppRoutes />
                    </Suspense>
                  </AppLayout>
                </Router>
              </ErrorProvider>
            </LocalizationProvider>
          </ThemeProvider>
        </PersistGate>
      </Provider>
    </ErrorBoundary>
  );
};

export default App;
