import React, { useState, useEffect, useCallback, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useDispatch, useSelector } from 'react-redux';
import { 
  Play, 
  ArrowRight, 
  ArrowLeft, 
  Check, 
  Target,
  Settings,
  RotateCw,
  Trophy,
  Home,
  RefreshCw,
  AlertCircle,
  CheckCircle,
  Activity,
  Zap
} from 'lucide-react';

// Import your existing hooks and services
import { useRobotController } from '../../hooks/useRobotController';
import { useAuth } from '../../hooks/useAuth';
import { submitChallenge } from '../../store/challengeSlice';

// Import your existing components
import { RobotVisualizer } from '../robot/RobotVisualizer';
import { LoadingSpinner } from '../common/LoadingSpinner';
import { ConnectionStatus } from '../common/ConnectionStatus';

const Challenge1 = ({ challenge, onComplete }) => {
  const dispatch = useDispatch();
  const { user } = useAuth();
  const { 
    isConnected, 
    currentAngles, 
    disableTorque,
    enableTorque,
    getCurrentJointAngles,
    getRelativeJointAngles
  } = useRobotController();
  
  // Challenge state management
  const [currentPage, setCurrentPage] = useState('welcome');
  const [currentPart, setCurrentPart] = useState(0);
  const [taskTypes, setTaskTypes] = useState(['absolute', 'relative', 'absolute']);
  const [taskAngles, setTaskAngles] = useState([null, null, null]);
  const [results, setResults] = useState([]);
  const [isEvaluating, setIsEvaluating] = useState(false);
  const [showTargetVisualization, setShowTargetVisualization] = useState(false);
  const [notification, setNotification] = useState(null);
  const [liveAngles, setLiveAngles] = useState([0, 0, 0]);
  
  // Constants
  const TOTAL_PARTS = 3;
  const TOLERANCE_DEGREES = 10;

  // Refs for timers
  const updateTimerRef = useRef(null);
  const notificationTimerRef = useRef(null);

  // Initialize challenge data
  useEffect(() => {
    initializeChallenge();
    return () => {
      if (updateTimerRef.current) clearInterval(updateTimerRef.current);
      if (notificationTimerRef.current) clearTimeout(notificationTimerRef.current);
    };
  }, []);

  // Real-time angle updates when on challenge page
  useEffect(() => {
    if (currentPage === 'challenge' && isConnected) {
      startAngleUpdates();
    } else {
      stopAngleUpdates();
    }
    
    return () => stopAngleUpdates();
  }, [currentPage, isConnected]);

  const initializeChallenge = useCallback(() => {
    setCurrentPart(0);
    setResults([]);
    setShowTargetVisualization(false);
    
    // Generate random task types and angles
    const shuffledTypes = ['absolute', 'relative'];
    shuffledTypes.push(Math.random() > 0.5 ? 'absolute' : 'relative');
    shuffledTypes.sort(() => Math.random() - 0.5);
    setTaskTypes(shuffledTypes);

    const angles = shuffledTypes.map(taskType => generateTaskAngles(taskType));
    setTaskAngles(angles);
  }, []);

  const generateTaskAngles = useCallback((taskType) => {
    if (taskType === 'absolute') {
      const firstAngle = 5 * Math.floor(Math.random() * 37);
      
      let secondAngle, thirdAngle;
      if (firstAngle > 0) {
        const minSecond = Math.max(-90, firstAngle - 90);
        const maxSecond = Math.min(90, firstAngle + 90);
        const countMinSecond = Math.floor(minSecond / 5);
        const countMaxSecond = Math.floor(maxSecond / 5);
        secondAngle = 5 * (Math.floor(Math.random() * (countMaxSecond - countMinSecond + 1)) + countMinSecond);
        
        if (secondAngle > -90) {
          const minThird = Math.max(-90, secondAngle - 90);
          const maxThird = Math.min(90, secondAngle + 90);
          const countMinThird = Math.floor(minThird / 5);
          const countMaxThird = Math.floor(maxThird / 5);
          thirdAngle = 5 * (Math.floor(Math.random() * (countMaxThird - countMinThird + 1)) + countMinThird);
        } else {
          secondAngle = 90;
          thirdAngle = 75;
        }
      } else {
        secondAngle = 5 * (Math.floor(Math.random() * 37) - 18);
        thirdAngle = 5 * (Math.floor(Math.random() * 37) - 18);
      }
      
      return [firstAngle, secondAngle, thirdAngle];
    } else {
      return [
        5 * Math.floor(Math.random() * 37),
        5 * (Math.floor(Math.random() * 37) - 18),
        5 * (Math.floor(Math.random() * 37) - 18)
      ];
    }
  }, []);

  const startAngleUpdates = useCallback(() => {
    if (updateTimerRef.current) return;
    
    updateTimerRef.current = setInterval(async () => {
      try {
        const taskType = taskTypes[currentPart];
        let angles;
        
        if (taskType === 'absolute') {
          angles = await getCurrentJointAngles();
        } else {
          angles = await getRelativeJointAngles();
        }
        
        const anglesDeg = angles.map(angle => angle * 180 / Math.PI);
        setLiveAngles(anglesDeg);
      } catch (error) {
        console.error('Error updating angles:', error);
      }
    }, 100);
  }, [currentPart, taskTypes, getCurrentJointAngles, getRelativeJointAngles]);

  const stopAngleUpdates = useCallback(() => {
    if (updateTimerRef.current) {
      clearInterval(updateTimerRef.current);
      updateTimerRef.current = null;
    }
  }, []);

  const showNotification = useCallback((message, type = 'info') => {
    setNotification({ message, type });
    
    if (notificationTimerRef.current) {
      clearTimeout(notificationTimerRef.current);
    }
    
    notificationTimerRef.current = setTimeout(() => {
      setNotification(null);
    }, 5000);
  }, []);

  const startChallenge = useCallback(async () => {
    try {
      await disableTorque();
      showNotification('Torque disabled. You can now move the robot manually.', 'success');
      setCurrentPage('challenge');
      setShowTargetVisualization(false);
    } catch (error) {
      console.error('Error starting challenge:', error);
      showNotification('Error starting challenge. Please check robot connection.', 'error');
    }
  }, [disableTorque, showNotification]);

  const completeCurrentTask = useCallback(async () => {
    if (isEvaluating) return;
    
    setIsEvaluating(true);
    stopAngleUpdates();
    
    try {
      await enableTorque();
      
      const taskType = taskTypes[currentPart];
      const targetAnglesForTask = taskAngles[currentPart];
      
      let currentAnglesRad;
      if (taskType === 'absolute') {
        currentAnglesRad = await getCurrentJointAngles();
      } else {
        currentAnglesRad = await getRelativeJointAngles();
      }
      
      const currentAnglesDeg = currentAnglesRad.map(angle => angle * 180 / Math.PI);
      
      const differences = currentAnglesDeg.map((current, i) => 
        Math.abs(current - targetAnglesForTask[i])
      );
      
      const maxDifference = Math.max(...differences);
      const success = maxDifference <= TOLERANCE_DEGREES;
      
      let score;
      if (maxDifference <= 5) {
        score = 100;
      } else if (maxDifference <= 10) {
        score = 80;
      } else if (maxDifference <= 20) {
        score = 60;
      } else {
        score = 40;
      }
      
      const result = {
        taskType,
        success,
        currentAngles: currentAnglesDeg,
        targetAngles: targetAnglesForTask,
        differences,
        maxDifference,
        score,
        feedback: generateFeedback(score, taskType)
      };
      
      setResults(prev => [...prev, result]);
      setShowTargetVisualization(true);
      
      showNotification(
        `Task ${currentPart + 1} completed! Score: ${score}/100`,
        success ? 'success' : 'warning'
      );
      
    } catch (error) {
      console.error('Error evaluating task:', error);
      showNotification('Error evaluating task. Please try again.', 'error');
    } finally {
      setIsEvaluating(false);
    }
  }, [
    isEvaluating, currentPart, taskTypes, taskAngles, 
    enableTorque, getCurrentJointAngles, getRelativeJointAngles,
    stopAngleUpdates, showNotification
  ]);

  const generateFeedback = useCallback((score, taskType) => {
    const taskLabel = taskType === 'absolute' ? 'positioning' : 'relative positioning';
    
    if (score >= 90) {
      return `Excellent precision in ${taskLabel}!`;
    } else if (score >= 75) {
      return `Good ${taskLabel}, with room for improvement.`;
    } else if (score >= 60) {
      return `Satisfactory ${taskLabel}, try to be more precise.`;
    } else {
      return `More practice needed to improve ${taskLabel} accuracy.`;
    }
  }, []);

  const proceedToNext = useCallback(async () => {
    if (currentPart < TOTAL_PARTS - 1) {
      setCurrentPart(prev => prev + 1);
      setShowTargetVisualization(false);
      
      try {
        await disableTorque();
        showNotification('Starting next task. Robot torque disabled.', 'info');
      } catch (error) {
        console.error('Error disabling torque:', error);
      }
    } else {
      await finishChallenge();
    }
  }, [currentPart, disableTorque, showNotification]);

  const finishChallenge = useCallback(async () => {
    const totalScore = results.reduce((sum, result) => sum + result.score, 0);
    const averageScore = totalScore / TOTAL_PARTS;
    
    try {
      // Submit to your existing Redux store
      const submission = {
        results,
        totalScore: averageScore,
        completed: true
      };
      
      await dispatch(submitChallenge({ 
        challengeId: 'C1', 
        submission 
      })).unwrap();
      
      setCurrentPage('results');
      
      // Call parent completion handler
      if (onComplete) {
        onComplete({
          success: averageScore >= 60,
          score: averageScore,
          results,
          challengeId: 'C1'
        });
      }
      
    } catch (error) {
      console.error('Error submitting challenge result:', error);
      showNotification('Challenge completed but failed to save results.', 'warning');
      setCurrentPage('results');
    }
  }, [results, dispatch, onComplete]);

  const resetChallenge = useCallback(() => {
    initializeChallenge();
    setCurrentPage('welcome');
  }, [initializeChallenge]);

  const getVisualizationAngles = useCallback((angles, taskType) => {
    if (taskType === 'relative') {
      return [
        angles[0],
        angles[0] + angles[1],
        angles[0] + angles[1] + angles[2]
      ];
    }
    return angles;
  }, []);

  // Animation variants
  const pageVariants = {
    initial: { opacity: 0, x: 20 },
    animate: { opacity: 1, x: 0 },
    exit: { opacity: 0, x: -20 }
  };

  // Render welcome page
  const renderWelcomePage = () => (
    <motion.div 
      className="min-h-screen bg-gradient-to-br from-gray-900 via-blue-900 to-purple-900 p-6"
      variants={pageVariants}
      initial="initial"
      animate="animate"
      exit="exit"
    >
      <div className="max-w-4xl mx-auto">
        <motion.div 
          className="text-center mb-8"
          initial={{ opacity: 0, y: -30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
        >
          <h1 className="text-4xl font-bold text-white mb-4 flex items-center justify-center gap-3">
            <Settings className="text-blue-400" size={40} />
            Challenge 1: Robot Configuration
          </h1>
          <p className="text-xl text-gray-300">Learn manual robot positioning and develop intuition about joint angles</p>
        </motion.div>

        <div className="grid lg:grid-cols-2 gap-8 items-start">
          <motion.div 
            className="bg-gray-800/50 backdrop-blur-sm rounded-xl p-6 border border-gray-700"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.3 }}
          >
            <h2 className="text-2xl font-semibold text-white mb-4">Welcome, Future Roboticist!</h2>
            <div className="text-gray-300 space-y-4">
              <p>
                Welcome to the Robot Configuration Challenge! You are a talented fresh recruit 
                in the robotics company AB6 Robotics who will lead challenging robotics projects.
              </p>
              <p>
                Before you take on robotics projects, the first 10 challenges are part of a 
                training program to gain proficiency in robotics.
              </p>
              <p>
                In this challenge, you will learn to manually position the robot to target 
                configurations and develop intuition about configurations and joint angles.
              </p>
            </div>
            
            <div className="mt-6 flex items-center gap-2 text-blue-400">
              <Activity size={20} />
              <span className="font-medium">Real-time robot control and visualization</span>
            </div>
          </motion.div>

          <motion.div 
            className="bg-gray-800/50 backdrop-blur-sm rounded-xl p-6 border border-gray-700"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.4 }}
          >
            <ConnectionStatus className="mb-4" />
            {RobotVisualizer && (
              <RobotVisualizer 
                currentAngles={[30, 45, 30]} 
                showTarget={false}
                className="w-full h-64"
              />
            )}
          </motion.div>
        </div>

        <motion.div 
          className="flex justify-center mt-8"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.5 }}
        >
          <button
            onClick={() => setCurrentPage('instructions')}
            className="flex items-center gap-2 px-8 py-3 bg-green-600 hover:bg-green-500 text-white rounded-lg font-semibold transition-colors"
          >
            Start Challenge
            <Play size={20} />
          </button>
        </motion.div>
      </div>
    </motion.div>
  );

  // Render instructions page
  const renderInstructionsPage = () => (
    <motion.div 
      className="min-h-screen bg-gradient-to-br from-gray-900 via-blue-900 to-purple-900 p-6"
      variants={pageVariants}
      initial="initial"
      animate="animate"
      exit="exit"
    >
      <div className="max-w-4xl mx-auto">
        <motion.h1 
          className="text-3xl font-bold text-white text-center mb-8"
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
        >
          Challenge Instructions
        </motion.h1>

        <motion.div 
          className="bg-gray-800/50 backdrop-blur-sm rounded-xl p-8 border border-gray-700 mb-8"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
        >
          <h2 className="text-2xl font-semibold text-white mb-6 flex items-center gap-3">
            <Target className="text-blue-400" />
            Challenge Overview
          </h2>
          
          <div className="text-gray-300 space-y-4">
            <p>
              In this challenge, you will demonstrate your ability to manually control the robot arm.
              The challenge consists of <strong className="text-white">three tasks</strong>:
            </p>
            
            <div className="grid md:grid-cols-2 gap-6 mt-6">
              <div className="bg-blue-900/30 rounded-lg p-4 border border-blue-700">
                <h3 className="font-semibold text-blue-400 mb-2">Absolute Positioning</h3>
                <p className="text-sm">Move the robot to specified absolute joint angles from the base reference.</p>
              </div>
              
              <div className="bg-purple-900/30 rounded-lg p-4 border border-purple-700">
                <h3 className="font-semibold text-purple-400 mb-2">Relative Positioning</h3>
                <p className="text-sm">Move the robot to specified relative joint angles between links.</p>
              </div>
            </div>
            
            <div className="mt-6">
              <h3 className="font-semibold text-white mb-3">Task Flow:</h3>
              <ol className="space-y-2 list-decimal list-inside text-gray-300">
                <li>Target angles will be displayed for each task</li>
                <li>Physically move the robot to the target position</li>
                <li>Press "Complete Task" when you're satisfied with positioning</li>
                <li>System evaluates your positioning accuracy</li>
                <li>Proceed to the next task</li>
              </ol>
            </div>
            
            <div className="bg-yellow-900/30 rounded-lg p-4 border border-yellow-700 mt-6">
              <div className="flex items-center gap-2 mb-2">
                <AlertCircle className="text-yellow-400" size={20} />
                <span className="font-semibold text-yellow-400">Scoring</span>
              </div>
              <p className="text-sm text-gray-300">
                Your score is based on positioning accuracy. Target tolerance: ±{TOLERANCE_DEGREES}°
              </p>
            </div>
          </div>
        </motion.div>

        <motion.div 
          className="flex justify-between items-center"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.3 }}
        >
          <button
            onClick={() => setCurrentPage('welcome')}
            className="flex items-center gap-2 px-6 py-3 bg-gray-700 hover:bg-gray-600 text-white rounded-lg transition-colors"
          >
            <ArrowLeft size={20} />
            Back to Welcome
          </button>
          
          <button
            onClick={startChallenge}
            disabled={!isConnected}
            className="flex items-center gap-2 px-8 py-3 bg-green-600 hover:bg-green-500 disabled:bg-gray-600 disabled:cursor-not-allowed text-white rounded-lg font-semibold transition-colors"
          >
            {!isConnected ? <Zap className="animate-pulse" size={20} /> : <Play size={20} />}
            {!isConnected ? 'Connecting...' : 'Start Challenge'}
          </button>
        </motion.div>
      </div>
    </motion.div>
  );

  // Render challenge page (similar to previous implementation but shortened for brevity)
  const renderChallengePage = () => {
    const taskType = taskTypes[currentPart];
    const targetAnglesForTask = taskAngles[currentPart];
    const currentResult = results[currentPart];

    return (
      <motion.div 
        className="min-h-screen bg-gradient-to-br from-gray-900 via-blue-900 to-purple-900 p-6"
        variants={pageVariants}
        initial="initial"
        animate="animate"
        exit="exit"
      >
        <div className="max-w-7xl mx-auto">
          <motion.div 
            className="flex justify-between items-center mb-6"
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
          >
            <h1 className="text-3xl font-bold text-white">
              Task {currentPart + 1} of {TOTAL_PARTS}
            </h1>
            
            <div className="flex gap-2">
              {Array.from({ length: TOTAL_PARTS }, (_, i) => (
                <div
                  key={i}
                  className={`w-3 h-3 rounded-full transition-colors ${
                    i < currentPart ? 'bg-green-500' :
                    i === currentPart ? 'bg-blue-500' : 'bg-gray-600'
                  }`}
                />
              ))}
            </div>
          </motion.div>

          <div className="grid lg:grid-cols-2 gap-8">
            <motion.div 
              className="space-y-6"
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
            >
              <div className="bg-gray-800/50 backdrop-blur-sm rounded-xl p-6 border border-gray-700">
                <h2 className="text-2xl font-semibold text-white mb-4 flex items-center gap-3">
                  <RotateCw className={`${taskType === 'absolute' ? 'text-blue-400' : 'text-purple-400'}`} />
                  {taskType === 'absolute' ? 'Absolute' : 'Relative'} Angle Task
                </h2>
                
                <div className={`rounded-lg p-4 border ${
                  taskType === 'absolute' 
                    ? 'bg-blue-900/30 border-blue-700' 
                    : 'bg-purple-900/30 border-purple-700'
                }`}>
                  <h3 className={`font-semibold mb-3 ${
                    taskType === 'absolute' ? 'text-blue-400' : 'text-purple-400'
                  }`}>
                    Target {taskType === 'absolute' ? 'Absolute' : 'Relative'} Angles:
                  </h3>
                  <div className="grid grid-cols-3 gap-4 text-center">
                    {targetAnglesForTask?.map((angle, i) => (
                      <div key={i} className="bg-gray-800/50 rounded p-3">
                        <div className="text-sm text-gray-400">Joint {i + 1}</div>
                        <div className="text-xl font-bold text-white">{angle}°</div>
                      </div>
                    ))}
                  </div>
                </div>
                
                {isConnected && (
                  <div className="bg-gray-900/50 rounded-lg p-4 border border-gray-600 mt-4">
                    <h3 className="font-semibold text-green-400 mb-3">Current Angles:</h3>
                    <div className="grid grid-cols-3 gap-4 text-center">
                      {liveAngles.map((angle, i) => (
                        <div key={i} className="bg-gray-800/50 rounded p-3">
                          <div className="text-sm text-gray-400">Joint {i + 1}</div>
                          <div className="text-xl font-bold text-white">{angle.toFixed(1)}°</div>
                        </div>
                      ))}
                    </div>
                  </div>
                )}

                {currentResult && (
                  <motion.div 
                    className="bg-gray-800/50 backdrop-blur-sm rounded-xl p-6 border border-gray-700 mt-4"
                    initial={{ opacity: 0, scale: 0.95 }}
                    animate={{ opacity: 1, scale: 1 }}
                  >
                    <h3 className="text-xl font-semibold text-white mb-4 flex items-center gap-2">
                      {currentResult.success ? (
                        <CheckCircle className="text-green-500" size={24} />
                      ) : (
                        <AlertCircle className="text-yellow-500" size={24} />
                      )}
                      Task Results
                    </h3>
                    
                    <div className="space-y-3">
                      <div className="flex justify-between items-center">
                        <span className="text-gray-300">Score:</span>
                        <span className={`font-bold text-xl ${
                          currentResult.score >= 80 ? 'text-green-400' :
                          currentResult.score >= 60 ? 'text-yellow-400' : 'text-red-400'
                        }`}>
                          {currentResult.score}/100
                        </span>
                      </div>
                      
                      <div className="text-sm text-gray-400">
                        Max Difference: {currentResult.maxDifference?.toFixed(1)}°
                      </div>
                      
                      <div className="text-sm text-gray-300 italic">
                        {currentResult.feedback}
                      </div>
                    </div>
                  </motion.div>
                )}
              </div>
            </motion.div>

            <motion.div 
              className="bg-gray-800/50 backdrop-blur-sm rounded-xl p-6 border border-gray-700"
              initial={{ opacity: 0, x: 20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ delay: 0.2 }}
            >
              <div className="flex justify-between items-center mb-4">
                <h3 className="text-xl font-semibold text-white">Robot Visualization</h3>
                <ConnectionStatus />
              </div>
              
              {RobotVisualizer && (
                <RobotVisualizer 
                  currentAngles={getVisualizationAngles(liveAngles, taskType)}
                  targetAngles={showTargetVisualization ? 
                    getVisualizationAngles(targetAnglesForTask || [0, 0, 0], taskType) : null
                  }
                  showTarget={showTargetVisualization}
                  className="w-full h-96"
                />
              )}
              
              <div className="mt-4 flex items-center justify-between text-sm">
                <div className="flex items-center gap-2">
                  <div className="w-3 h-3 bg-blue-500 rounded"></div>
                  <span className="text-gray-300">Current Position</span>
                </div>
                {showTargetVisualization && (
                  <div className="flex items-center gap-2">
                    <div className="w-3 h-3 bg-orange-500 rounded opacity-60"></div>
                    <span className="text-gray-300">Target Position</span>
                  </div>
                )}
              </div>
            </motion.div>
          </div>

          <motion.div 
            className="flex justify-between items-center mt-8"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.4 }}
          >
            <button
              onClick={() => setCurrentPage('instructions')}
              className="flex items-center gap-2 px-6 py-3 bg-gray-700 hover:bg-gray-600 text-white rounded-lg transition-colors"
            >
              <ArrowLeft size={20} />
              Back to Instructions
            </button>
            
            <div className="flex gap-4">
              {!currentResult ? (
                <button
                  onClick={completeCurrentTask}
                  disabled={isEvaluating || !isConnected}
                  className="flex items-center gap-2 px-8 py-3 bg-green-600 hover:bg-green-500 disabled:bg-gray-600 disabled:cursor-not-allowed text-white rounded-lg font-semibold transition-colors"
                >
                  {isEvaluating ? (
                    <>
                      <RefreshCw className="animate-spin" size={20} />
                      Evaluating...
                    </>
                  ) : (
                    <>
                      <Check size={20} />
                      Complete Task
                    </>
                  )}
                </button>
              ) : (
                <button
                  onClick={proceedToNext}
                  className="flex items-center gap-2 px-8 py-3 bg-blue-600 hover:bg-blue-500 text-white rounded-lg font-semibold transition-colors"
                >
                  {currentPart < TOTAL_PARTS - 1 ? (
                    <>
                      Next Task
                      <ArrowRight size={20} />
                    </>
                  ) : (
                    <>
                      View Results
                      <Trophy size={20} />
                    </>
                  )}
                </button>
              )}
            </div>
          </motion.div>
        </div>
      </motion.div>
    );
  };

  // Render results page
  const renderResultsPage = () => {
    const totalScore = results.reduce((sum, result) => sum + result.score, 0);
    const averageScore = totalScore / TOTAL_PARTS;
    
    return (
      <motion.div 
        className="min-h-screen bg-gradient-to-br from-gray-900 via-blue-900 to-purple-900 p-6"
        variants={pageVariants}
        initial="initial"
        animate="animate"
        exit="exit"
      >
        <div className="max-w-5xl mx-auto">
          <motion.div 
            className="text-center mb-8"
            initial={{ opacity: 0, y: -30 }}
            animate={{ opacity: 1, y: 0 }}
          >
            <h1 className="text-4xl font-bold text-white mb-4 flex items-center justify-center gap-3">
              <Trophy className="text-yellow-400" size={40} />
              Challenge Complete!
            </h1>
            <p className="text-xl text-gray-300">Robot Configuration Challenge Results</p>
          </motion.div>

          <motion.div 
            className="bg-gray-800/50 backdrop-blur-sm rounded-xl p-8 border border-gray-700 mb-8"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
          >
            <div className="text-center mb-6">
              <div className="text-6xl font-bold mb-2 text-blue-400">
                {averageScore.toFixed(1)}
              </div>
              <div className="text-gray-400">Average Score</div>
            </div>
            
            <div className="text-center mb-6">
              <h2 className="text-2xl font-semibold mb-2 text-green-400">
                {averageScore >= 90 ? 'Outstanding Performance!' :
                 averageScore >= 75 ? 'Good Performance!' :
                 averageScore >= 60 ? 'Fair Performance' : 'More Practice Needed'}
              </h2>
              <p className="text-gray-300">
                {averageScore >= 90 ? "You've demonstrated exceptional precision in Robot Configuration." :
                 averageScore >= 75 ? "You've shown solid skills in Robot Configuration." :
                 averageScore >= 60 ? "You have the basic skills but need more practice." :
                 "Keep practicing to develop the skills needed for robotics projects."}
              </p>
            </div>
            
            <div className="w-full bg-gray-700 rounded-full h-4 mb-4">
              <div 
                className="h-4 rounded-full transition-all duration-300 bg-blue-500"
                style={{ width: `${averageScore}%` }}
              />
            </div>
          </motion.div>

          <motion.div 
            className="grid md:grid-cols-3 gap-6 mb-8"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.3 }}
          >
            {results.map((result, index) => (
              <motion.div
                key={index}
                className="bg-gray-800/50 backdrop-blur-sm rounded-xl p-6 border border-gray-700"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.4 + index * 0.1 }}
              >
                <div className="text-center mb-4">
                  <h3 className="text-lg font-semibold text-white mb-2">
                    Task {index + 1}
                  </h3>
                  <div className={`text-sm px-3 py-1 rounded-full inline-block ${
                    result.taskType === 'absolute' 
                      ? 'bg-blue-900/50 text-blue-400 border border-blue-700'
                      : 'bg-purple-900/50 text-purple-400 border border-purple-700'
                  }`}>
                    {result.taskType === 'absolute' ? 'Absolute' : 'Relative'} Positioning
                  </div>
                </div>
                
                <div className="text-center mb-4">
                  <div className={`text-3xl font-bold mb-1 ${
                    result.score >= 80 ? 'text-green-400' :
                    result.score >= 60 ? 'text-yellow-400' : 'text-red-400'
                  }`}>
                    {result.score}
                  </div>
                  <div className="text-gray-400 text-sm">Score</div>
                </div>
                
                <div className="space-y-2 text-sm text-gray-300">
                  <div className="flex justify-between">
                    <span>Max Error:</span>
                    <span>{result.maxDifference?.toFixed(1)}°</span>
                  </div>
                  <div className="flex justify-between">
                    <span>Status:</span>
                    <span className={result.success ? 'text-green-400' : 'text-red-400'}>
                      {result.success ? 'Success' : 'Failed'}
                    </span>
                  </div>
                </div>
                
                <div className="mt-4 text-xs text-gray-400 italic">
                  {result.feedback}
                </div>
              </motion.div>
            ))}
          </motion.div>

          <motion.div 
            className="flex justify-between items-center"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.7 }}
          >
            <button
              onClick={resetChallenge}
              className="flex items-center gap-2 px-6 py-3 bg-gray-700 hover:bg-gray-600 text-white rounded-lg transition-colors"
            >
              <RefreshCw size={20} />
              Try Again
            </button>
            
            <button
              onClick={() => onComplete && onComplete({
                success: averageScore >= 60,
                score: averageScore,
                results,
                challengeId: 'C1'
              })}
              className="flex items-center gap-2 px-8 py-3 bg-blue-600 hover:bg-blue-500 text-white rounded-lg font-semibold transition-colors"
            >
              <Home size={20} />
              Back to Dashboard
            </button>
          </motion.div>
        </div>
      </motion.div>
    );
  };

  return (
    <div className="relative">
      <AnimatePresence mode="wait">
        {currentPage === 'welcome' && renderWelcomePage()}
        {currentPage === 'instructions' && renderInstructionsPage()}
        {currentPage === 'challenge' && renderChallengePage()}
        {currentPage === 'results' && renderResultsPage()}
      </AnimatePresence>
      
      {/* Notification */}
      <AnimatePresence>
        {notification && (
          <motion.div
            className={`fixed top-4 right-4 z-50 p-4 rounded-lg shadow-lg max-w-sm ${
              notification.type === 'success' ? 'bg-green-600' :
              notification.type === 'error' ? 'bg-red-600' :
              notification.type === 'warning' ? 'bg-yellow-600' : 'bg-blue-600'
            } text-white`}
            initial={{ opacity: 0, x: 100 }}
            animate={{ opacity: 1, x: 0 }}
            exit={{ opacity: 0, x: 100 }}
          >
            <div className="flex items-center gap-2">
              {notification.type === 'success' && <CheckCircle size={20} />}
              {notification.type === 'error' && <AlertCircle size={20} />}
              {notification.type === 'warning' && <AlertCircle size={20} />}
              {notification.type === 'info' && <Activity size={20} />}
              <span className="text-sm">{notification.message}</span>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default Challenge1;
