// src/utils/constants.js
export const API_ENDPOINTS = {
  AUTH: {
    LOGIN: '/api/v1/auth/login',
    REGISTER: '/api/v1/auth/register',
    LOGOUT: '/api/v1/auth/logout',
    ME: '/api/v1/auth/me',
    PROFILE: '/api/v1/auth/profile'
  },
  CHALLENGES: {
    LIST: '/api/v1/challenges',
    DETAIL: '/api/v1/challenges',
    START: '/api/v1/challenges',
    SUBMIT: '/api/v1/challenges',
    HISTORY: '/api/v1/challenges',
    LEADERBOARD: '/api/v1/challenges'
  },
  ROBOT: {
    STATUS: '/api/v1/robot/status',
    CONNECT: '/api/v1/robot/connect',
    DISCONNECT: '/api/v1/robot/disconnect',
    COMMAND: '/api/v1/robot/command',
    JOINTS: '/api/v1/robot/joint-angles'
  },
  USERS: {
    PROGRESS: '/api/v1/users/progress',
    LEADERBOARD: '/api/v1/users/leaderboard'
  }
};

export const CHALLENGE_TYPES = {
  MANUAL_MOVEMENT: 'manual_movement',
  PD_CONTROL: 'pd_control',
  TRAJECTORY_PLANNING: 'trajectory_planning',
  INVERSE_KINEMATICS: 'inverse_kinematics',
  QUIZ: 'quiz',
  WORKSPACE_ANALYSIS: 'workspace_analysis'
};

export const DIFFICULTY_LEVELS = {
  BEGINNER: 'beginner',
  INTERMEDIATE: 'intermediate',
  ADVANCED: 'advanced',
  EXPERT: 'expert'
};

export const ROBOT_COMMANDS = {
  MOVE_JOINT: 'move_joint',
  MOVE_TO_POSITION: 'move_to_position',
  SET_TORQUE: 'set_torque',
  HOME_POSITION: 'home_position',
  EMERGENCY_STOP: 'emergency_stop',
  GET_STATUS: 'get_status',
  ENABLE_TORQUE: 'enable_torque',
  DISABLE_TORQUE: 'disable_torque'
};

export const CONNECTION_STATUS = {
  DISCONNECTED: 'disconnected',
  CONNECTING: 'connecting',
  CONNECTED: 'connected',
  ERROR: 'error'
};

export const JOINT_LIMITS = {
  1: { min: -150, max: 150, name: 'Base' },
  2: { min: -90, max: 90, name: 'Shoulder' },
  3: { min: -90, max: 90, name: 'Elbow' }
};

export const SCORE_THRESHOLDS = {
  EXCELLENT: 90,
  GOOD: 80,
  FAIR: 70,
  POOR: 60
};

export const RANK_THRESHOLDS = {
  ROBOTICS_MASTER: { challenges: 10, avgScore: 90 },
  EXPERT_ENGINEER: { challenges: 8, avgScore: 85 },
  SENIOR_DEVELOPER: { challenges: 6, avgScore: 80 },
  JUNIOR_DEVELOPER: { challenges: 4, avgScore: 75 },
  APPRENTICE: { challenges: 2, avgScore: 70 },
  BEGINNER: { challenges: 1, avgScore: 60 },
  ROOKIE: { challenges: 0, avgScore: 0 }
};

export const WEBSOCKET_EVENTS = {
  ROBOT_STATUS: 'robot_status',
  CHALLENGE_UPDATE: 'challenge_update',
  ERROR: 'error',
  PING: 'ping',
  PONG: 'pong',
  CONNECTION: 'connection',
  DISCONNECTION: 'disconnection'
};

// =================================================================
